@import "./helpers/variables";
@import "./helpers/mixins";
.pg-wrapper {
  height: 100% !important;
}

.main-screen {
  background-image: url("../images/main-screen/background.png");
  background-repeat: no-repeat;
  background-size: cover;
  background-position: center;
  padding: 0.8em;
  position: relative;
  overflow-x: hidden;
  width: 100%;
  height: 100vh;
  @media (min-width: $desktop-bp) {
    height: 100%;
    padding: 1.5em;
    overflow: unset;
  }
}

.main-screen__inner {
  z-index: 1;
  padding: 0.7em 0.5em;
  border: 10px solid #fff;
  position: relative;
  height: 100%;
  @media (min-width: $desktop-bp) {
    padding: 1.5em 3.5em;
    border: 20px solid #fff;
  }
}

.main-screen__burger {
  width: 30px;
  cursor: pointer;
  position: absolute;
  top: 20px;
  right: 20px;
  transition: 0.2s linear;
  @media (min-width: $desktop-bp) {
    width: 40px;
    top: 55px;
    right: 42px;
  }

  &:hover li:nth-child(2),
  &:hover li:last-child {
    width: 100%;
  }

  li {
    transition: 0.2s linear;
    width: 100%;
    height: 4px;
    background-color: #000;

    &:first-child {
      width: 100%;
    }

    &:nth-child(2) {
      width: 70%;
    }

    &:last-child {
      width: 40%;
    }

    &:not(:last-child) {
      margin-bottom: 4px;
    }
  }
}

.women-picture {
  width: 66%;
  position: absolute;
  top: 0;
  right: 50%;
  transform: translate(50%, -23%);
  @media (min-width: $desktop-bp) {
    width: 30%;
  }
}

.main-screen__logo {
  width: 65px;
  @media (min-width: $desktop-bp) {
    width: 165px;
  }
}

.main-screen__title-wrapper {
  width: 260px;
  text-align: center;
  position: absolute;
  top: 20%;
  left: 50%;
  transform: translateX(-50%);
  @media (min-width: $desktop-bp) {
    width: 900px;
    height: 145px;
  }
}

.main-screen__title {
  @media (min-width: $desktop-bp) {
    width: 100%;
    margin-bottom: 1.5em;
  }
}

.main-screen__subtitle {
  @include text($font-size: 12px);
  @media (min-width: $desktop-bp) {
    font-size: 23px;
    letter-spacing: 14px;
    text-align: center;
    cursor: default;
  }
}

