  @media (min-width: $desktop-bp) {
    transform: translate(-15%, 15%) !important;
  }
  li {
    position: unset !important;
    &:first-child {
      width: calc(25px + 2vh);
      position: absolute !important;
      top: 0;
      margin-left: 7em;
    }
    &:nth-child(2) {
      width: calc(50px + 2vh);
      margin: 3em 3em 0;
