    &:nth-child(3) {
      width: calc(110px + 2vh);
    }
    &:nth-child(4) {
      width: calc(170px + 2vh);
    }
    @media (min-width: $desktop-bp) {
      &:first-child {
        width: 80px;
        position: absolute !important;
        bottom: 0;
        margin-left: 7em;
      }
      &:nth-child(2) {
        width: 140px;
        margin: 3em 3em 0;
      }
      &:nth-child(3) {
        width: 250px;
      }
      &:nth-child(4) {
        width: 390px;
      }
    }
  }
}

.fruits__right-list {
