    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    margin-bottom: 96px;

    & > .women-picture-wrapper {
      width: 100%;
      height: 100%;
      & > img {
        margin: 0 auto;
        width: 50%;
      }
    }

    & .comments__author {
      @include text($font-size: calc(75px + 0.5vw), $font-weight: 700);
      background: $primary-text-gradient;
      background-clip: text;
      -webkit-background-clip: text;
      -webkit-text-fill-color: transparent;
      text-align: center;
      margin-bottom: 30px;
    }

    & .ig-stats {
      display: flex;
      margin-bottom: 40px;

      &__link {
        margin-right: 20px;
        padding: 0 20px 0 50px;
        border-right: 2px solid #c5c5c5e1;
        position: relative;

        &::before {
          content: "";
          display: inline-block;
          position: absolute;
          background-image: url("../images/instagram-icon.svg");
          width: 30px;
          height: 30px;
          top: 50%;
          transform: translateY(-50%);
          left: 5%;
        }

        & > a {
          @extend .comments__author;
          font-size: calc(16px + 0.5vw);
          margin: 0;
        }
      }
      &__followers {
        & > strong {
          @extend .comments__author;
          font-size: calc(16px + 0.5vw);
          margin-right: 5px;
        }

        & > span {
          @include text($color: #535252, $font-size: calc(12px + 0.5vw));
        }
      }
    }

    & > p {
      @include text($font-size: calc(10px + 0.5vw));
      line-height: 33px;
    }
  }
}
