@import "./helpers/variables";
@import "./helpers/mixins";

//Using "!important" only for overwriting Parallax.js positions styles!

.product-screen {
  position: relative;
  & > .vines {
    opacity: 1;
    width: 100%;
    position: absolute;
    top: 0;
    left: 0;
    transform: translateY(-30%);
    z-index: -1;
    visibility: visible;
    &.hidden {
      opacity: 0;
      visibility: hidden;
    }
  }
  &__parrot {
    width: 125px;
    height: 125px;
    position: absolute;
    top: 20% !important;
    left: 12% !important;
  }
  &__vitamins {
    max-width: 55%;
    width: 55%;
    height: 57%;
    background-repeat: no-repeat;
    background-size: cover;
    position: absolute;
    top: 15% !important;
    left: 20% !important;
    z-index: 2;

    &--lg {
      max-width: 80%;
      width: 100%;
      top: 20% !important;
      left: 10% !important;
    }
  }

  &__plants {
    z-index: -1;
    max-width: 200%;
    position: absolute;
    top: 0;
    left: -20% !important;
  }
  &__reef {
    max-width: 100%;
    z-index: -1;
    position: absolute;
    top: -10% !important;
    left: -10% !important;
  }
  &__fish {
    @extend .product-screen__parrot;
    top: 0% !important;
    left: 70% !important;
  }

  &__night {
    @extend .product-screen__reef;
    width: 650px;
    z-index: -1;
    top: -7% !important;
    left: 16% !important;
  }

  &__owl {
    top: 20% !important;
    left: 68% !important;
    position: absolute;
    width: 90px;
    height: 90px;
  }
  &__forest {
    @extend .product-screen__reef;
    max-width: 105%;
    left: -11% !important;
    top: -13% !important;
  }
  &__bug {
    @extend .product-screen__owl;
  }
  &__planet {
    border-radius: 50%;
    overflow: hidden;
    position: absolute !important;
    top: 9% !important;
    transform: translate(0, 140%) !important;
    & > div {
      width: 7.5vw;
      height: 7.5vw;
      background-image: url("../images/products-screens/omega/planet.svg");
      background-repeat: no-repeat;
      background-size: 200% 200%;
      animation: planetRotation 5s linear infinite alternate-reverse;
      transition: transform 200ms linear;
    }
  }
  &__spaceship {
    @extend .product-screen__owl;
    top: 5% !important;
    left: 60% !important;
    width: 120px;
  }

  &__space {
    @extend .product-screen__reef;
    max-width: 150%;
    top: -5% !important;
  }

  &__ballons {
    width: 190px;
    position: absolute !important;
    top: 0% !important;
    left: 10% !important;
  }

  &__city {
    @extend .product-screen__reef;
    top: -5% !important;
    left: -1% !important;
  }
}

@keyframes planetRotation {
  from {
    background-position: 85% 20%;
  }
  to {
    background-position: 10% 80%;
  }
}

.product-screen__content {
  height: 100%;
  display: flex;
}

.product-screen__pictures {
  user-select: none;
  opacity: 1;
  position: relative;
  left: 1% !important;
  top: 10% !important;
  width: 100%;
  height: 100%;
  transition: 0.5s all;
  &.hidden {
    opacity: 0;
  }
}

.product-screen__vitamins {
  &--calcivit {
    background-image: url("../images/products-screens/calcivit/vitamins.png");
    @media (-webkit-min-device-pixel-ratio: 2), (min-resolution: 192dpi) {
      background-image: url("../images/products-screens/calcivit/vitamins@2x.png");
    }
  }
  &--multivit {
    background-image: url("../images/products-screens/multivit/vitamins.png");
    @media (-webkit-min-device-pixel-ratio: 2), (min-resolution: 192dpi) {
      background-image: url("../images/products-screens/multivit/vitamins@2x.png");
    }
  }
  &--immunovit {
    background-image: url("../images/products-screens/immunovit/vitamins.png");
    @media (-webkit-min-device-pixel-ratio: 2), (min-resolution: 192dpi) {
      background-image: url("../images/products-screens/immunovit/vitamins@2x.png");
    }
  }
  &--smartvit {
    background-image: url("../images/products-screens/smartvit/vitamins.png");
    @media (-webkit-min-device-pixel-ratio: 2), (min-resolution: 192dpi) {
      background-image: url("../images/products-screens/smartvit/vitamins@2x.png");
    }
  }
  &--omega {
    background-image: url("../images/products-screens/omega/vitamins.png");
    @media (-webkit-min-device-pixel-ratio: 2), (min-resolution: 192dpi) {
      background-image: url("../images/products-screens/omega/vitamins@2x.png");
    }
  }
}

.product-screen__desc {
  width: 100%;
  z-index: 2;
  padding: 0 0 0 5em;
  align-self: center;
}

.product-screen__title {
  font-size: 73px;
  color: #403e3e;
  font-family: $primary-font-stack;
  font-weight: 700;
  text-transform: uppercase;
  margin-bottom: 0.6em;
}

.product-screen__text {
  width: 600px;
  font-size: 24px;
  line-height: 36px;
  color: #403e3e;
  font-family: $primary-font-stack;
  font-weight: 400;
  margin-bottom: 3.5em;

  & strong {
    font-weight: 600;
  }
}

.product-screen__btn {
  font-family: $primary-font-stack;
  font-size: 22px;
  font-weight: 800;
  border: 5px solid transparent;
  border-radius: 44px;
  background-color: #ffffff;
  padding: 1.2em 2.2em 1.2em 6em;
  position: relative;
  display: inline-block;
  text-transform: uppercase;
  cursor: pointer;
  transition: 0.2s linear;

  &::before {
    content: "";
    display: inline-block;
    border: 6px solid $primary-bg-color;
    border-radius: 50%;
    position: absolute;
    background-position: center;
    background-size: cover;
    background-repeat: no-repeat;
    width: 90px;
    height: 90px;
    top: 0;
    left: 0;
    transform: translate(16%, -35%);
  }

  &::after {
    content: "";
    display: inline-block;
    background-repeat: no-repeat;
    background-position: center;
    position: absolute;
    top: 50%;
    right: 5%;
    transform: translate(-50%, -40%);
    width: 18px;
    height: 18px;
  }
  &--green {
    color: $green-btn;
    &:hover {
      border-color: $green-btn;
