@import "./helpers/variables";
@import "./helpers/mixins";

.product-screen {
  position: relative;
  & > .vines {
    opacity: 1;
    width: 100%;
    position: absolute;
    top: 0;
    left: 0;
    transform: translateY(-30%);
    z-index: -1;
    visibility: visible;
    &.hidden {
      opacity: 0;
      visibility: hidden;
    }
  }
  &__parrot {
    width: 125px;
    height: 125px;
    position: absolute;
    top: 20% !important;
    left: 12% !important;
  }
  &__bottle {
    width: 500px;
    position: absolute;
    top: 15% !important;
    left: 20% !important;
    z-index: 2;
  }
  &__plants {
    z-index: -1;
    width: 950px;
    position: absolute;
    top: 0;
    left: -15% !important;
  }
  &__reef {
    z-index: -1;
    position: absolute;
    top: -20% !important;
    left: -15% !important;
  }
  &__fish {
    @extend .product-screen__parrot;
    top: 0% !important;
    left: 70% !important;
  }

  &__forest {
    @extend .product-screen__reef;
    width: 650px;
    z-index: -1;
    top: -7% !important;
    left: 16% !important;
  }

  &__owl {
    top: 20% !important;
    left: 68% !important;
    position: absolute;
    width: 90px;
    height: 90px;
  }
}

.product-screen__content {
  height: 100%;
  display: flex;
}

.product-screen__pictures {
  opacity: 1;
  position: relative;
  left: 1% !important;
  top: 10% !important;
  width: 100%;
  height: 100%;
  transition: 0.5s all;
  &.hidden {
    opacity: 0;
  }
}

.product-screen__desc {
  width: 100%;
  padding: 0 0 0 5em;
  align-self: center;
}

.product-screen__title {
  font-size: 73px;
  color: #403e3e;
  font-family: $primary-font-stack;
  font-weight: 700;
  text-transform: uppercase;
  margin-bottom: 0.6em;
}

.product-screen__text {
  width: 600px;
  font-size: 24px;
  line-height: 36px;
  color: #403e3e;
  font-family: $primary-font-stack;
  font-weight: 400;
  margin-bottom: 3.5em;

  & strong {
    font-weight: 600;
  }
}

