    display: inline-block;
    position: absolute;
    background-position: center;
    background-size: contain;
    background-repeat: no-repeat;
    width: 90px;
    height: 90px;
    top: 0;
    left: 0;
    transform: translate(16%, -35%);
  }

  &::after {
    content: "";
    display: inline-block;
    background-repeat: no-repeat;
    background-position: center;
    position: absolute;
    top: 50%;
    right: 5%;
