@import "./slider-control";
@import "./helpers/variables";
@import "./helpers/mixins";

.border {
  border: 13px solid #fff;
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  z-index: 1;
  width: 100%;
  height: 100%;
  @media (min-width: $laptop-sm-bp) {
    padding: 1.5em;
    border: 20px solid #fff;
  }
}

.main-page__screen {
  user-select: none;
  padding: 0.8em;
  position: relative;
  overflow: hidden;
  width: 100%;
  height: unset;
  background-color: $primary-bg-color;
  @media (min-width: $laptop-sm-bp) {
    padding: 1.5em;
    height: 100%;
  }

  @media (min-width: 1024px) and (min-height: 1366px) {
    height: 40vh;
  }

  @media (min-width: $desktop-sm-bp) {
    height: 100vh;
  }

  &.main-page__about {
    background: none;
  }
}

.main-page__inner {
  z-index: 1;
  padding: 0.7em 0.5em;
  position: relative;
  height: 100%;

  @media (min-width: $desktop-sm-bp) {
    // padding: 3.5em 5.5em;
  }
  & > nav {
    width: 100%;
    height: 100%;
    background-color: #fff;
    z-index: 23;
    position: absolute;
    top: 0;
    left: 0;
    display: flex;
    justify-content: center;
    align-items: center;
    visibility: visible;

    @media (min-width: $laptop-sm-bp) {
      background-image: url("../images/menu/decor.png");
      background-repeat: no-repeat;
      background-position: -40px 0px;
      background-size: contain;
    }

    &.hidden {
      visibility: hidden;
    }

    & > ul {
      & > li {
        width: 80%;
        margin: 0 auto 60px auto;
        margin-bottom: 60px;
        position: relative;

        @media (min-width: $landscape-bp) {
          margin-bottom: 40px;
        }

        @media (min-width: $tablet-bp) {
          width: 100%;
          margin: 0 0 60px 0;
        }

        &:last-child {
          @media (min-width: $landscape-bp) {
            margin-bottom: 0;
          }
        }

        &::before {
          content: "";
          background-image: url("../images/menu/1.png");
          background-repeat: no-repeat;
          background-size: contain;
          background-position: center;
          width: 50px;
          height: 50px;
          position: absolute;
          top: -35%;
          left: -5%;
          z-index: -1;

          @media (min-width: $landscape-bp) {
            width: 35px;
            height: 35px;
          }

          @media (min-width: $tablet-bp) {
            width: 50px;
            height: 50px;
          }
        }
        &:hover::before {
          animation: bounce 0.7s ease-in-out;
        }
        &:nth-child(2)::before {
          background-image: url("../images/menu/2.png");
        }
        &:nth-child(3)::before {
          background-image: url("../images/menu/3.png");
        }
        &:nth-child(4)::before {
          background-image: url("../images/menu/4.png");
        }
        &:last-child::before {
          background-image: url("../images/menu/5.png");
        }

        & > a {
          @include text(
            $color: #403e3e,
            $font-size: calc(26px + 0.5vw),
            $font-weight: 700
          );
          z-index: 1;

          @media (min-width: $landscape-bp) {
            font-size: calc(19px + 0.5vw);
          }

          @media (min-width: $tablet-bp) {
            font-size: calc(45px + 0.5vw);
          }
        }
      }
    }
  }
}

.main-page__logo {
  user-select: none;
  position: absolute;
  top: 25px;
  left: 25px;
  z-index: 2;
  max-width: 10%;
  width: 100%;
  @media (min-width: $laptop-sm-bp) {
    top: 45px;
    left: 80px;
  }

  &.mobile-logo {
    @media (max-width: $laptop-sm-bp) {
      display: none;
    }
  }
}

.main-page__fruits {
  display: none;
  position: absolute;
  top: 0;
  left: 0;
  bottom: 100%;
  transform: translate(0, 50%);
  width: 100%;
  justify-content: space-between;
  @media (min-width: $tablet-bp) {
    display: flex;
    top: -5%;
  }
  @media (min-width: $desktop-lg-bp) {
    top: 9%;
  }

  & > ul:first-child {
    position: absolute;
    top: 0;
    left: 0;
    transform: translate(-25%, 15%) !important;

    @media (min-width: $laptop-sm-bp) {
      transform: translate(-15%, 15%) !important;
    }
    li {
      position: unset !important;
      &:first-child {
        position: absolute !important;
        top: 0;
        margin-left: 7em;
        max-width: 14%;
      }
      &:nth-child(2) {
        margin: 3em 3em 0;
        max-width: 27%;
      }
      &:nth-child(3) {
        max-width: 55%;
      }
      &:nth-child(4) {
        max-width: 85%;
      }
      @media (min-width: $tablet-bp) {
        &:first-child {
          position: absolute !important;
          bottom: 0;
          margin-left: 7em;
        }
        &:nth-child(2) {
          margin: 3em 3em 0;
        }
      }
    }
  }

  & > ul:last-child {
    position: absolute;
    top: 0;
    right: -100px;
    transform: translate(25%, 15%) !important;

    @media (min-width: $laptop-sm-bp) {
      transform: translate(20%, 15%) !important;
    }
    li {
      position: unset !important;

      &:first-child {
        max-width: 12%;
        margin-left: 12em;
      }
      &:nth-child(2) {
        max-width: 25%;
        margin-left: 7em;
      }
      &:nth-child(3) {
        max-width: 40%;
        margin-left: 5em;
      }
      &:nth-child(4) {
        max-width: 85%;
      }
      @media (min-width: $tablet-bp) {
        &:first-child {
          margin-left: 12em;
        }
        &:nth-child(2) {
          margin-left: 9em;
        }
        &:nth-child(3) {
          margin-left: 5em;
        }
      }
    }
  }
}

.main-page__footer {
  background-image: url("../images/about-products/background.jpg");
}

.main-page__slider-control {
  @extend .slider-control;
  top: 55%;
}

@keyframes bounce {
  0%,
  20%,
  60%,
  100% {
    -webkit-transform: translateY(0);
    transform: translateY(0);
  }

  40% {
    -webkit-transform: translateY(-20px);
    transform: scale(1.1) translateY(-20px);
  }

  80% {
    -webkit-transform: translateY(-10px);
    transform: scale(1.05) translateY(-10px);
  }
}

sources.scss\\components\\about-product.scss:
@import "./main-page.scss";
@import "../helpers/variables";
@import "../helpers/mixins";
.about-product__inner {
  background-color: #fff;
  height: 100%;
  padding: 1.7em 1em;

  // @media (min-width: $tablet-bp) {
  //   padding: 2.7em 5em;
  // }

  // @media (min-width: $desktop-sm-bp) {
  //   padding: 5.5em 9.5em;
  // }

  @media (min-width: $desktop-lg-bp) {
    padding: 3.7em 5em;
  }
}

.about-product__title {
  @include text($font-size: calc(35px + 0.5vw), $font-weight: 600);
  margin-bottom: 0.8em;

  @media (min-width: $desktop-sm-bp) {
    font-size: calc(75px + 0.5vw);
    margin-bottom: 1em;
  }
}

.about-product__subtitle {
  @include text($font-size: calc(25px + 0.5vw), $font-weight: 600);
  padding-bottom: 1em;
  border-bottom: 5px solid $primary-bg-color;
  @media (min-width: $desktop-sm-bp) {
    font-size: calc(40px + 0.5vw);
  }
}

.about-product__content {
  display: flex;
  flex-wrap: wrap;
  justify-content: space-between;
  flex-direction: column;
  width: 100%;
  align-items: baseline;

  @media (min-width: $desktop-sm-bp) {
    flex-direction: row;
  }
}

.about-product__desc {
  flex-basis: calc(50% - 5em);
  margin-bottom: 2em;

  @media (min-width: $landscape-bp) {
    width: 100%;
  }

  @media (min-width: $desktop-sm-bp) {
    margin: 0;
  }

  & div {
    @media (min-width: $tablet-bp) {
      overflow: auto;
      height: 100%;
    }

    @media (min-width: $desktop-sm-bp) {
      height: 310px;
    }

    @media (min-width: $desktop-md-bp) {
      height: 560px;
    }

    @media (min-width: 1366px) and (min-height: 1024px) {
      height: 100%;
      overflow: unset;
    }

    @media (min-width: 1024px) and (min-height: 1366px) {
      height: 100%;
    }

    & > p {
      @include text($font-size: calc(11px + 0.5vw));
      line-height: 33px;
      padding-top: 25px;
      margin-bottom: 15px;

      @media (min-width: $desktop-sm-bp) {
        line-height: 40px;
      }
    }
    & > ul {
      list-style: square inside;

      & > li {
        @include text($font-size: calc(11px + 0.5vw));
        margin-bottom: 15px;
        line-height: 33px;
        margin-left: 30px;
      }
    }
  }
}

::-webkit-scrollbar {
  width: 6px;
}

::-webkit-scrollbar-thumb {
  background: linear-gradient(
    135deg,
    rgba(61, 165, 107, 1) 10%,
    rgba(61, 165, 107, 1) 25%,
    rgba(69, 125, 171, 1) 30%,
    rgba(69, 125, 171, 1) 50%,
    rgba(171, 79, 135, 1) 60%,
    rgba(171, 79, 135, 1) 70%,
    rgba(223, 67, 7, 1) 90%,
    rgba(223, 67, 7, 1) 100%
  );
}

.about-product__ingredients {
  @extend .about-product__desc;
  width: 100%;
  & > p {
    @include text($font-size: calc(13px + 0.5vw));
    padding-top: 25px;
    margin-bottom: 1em;
  }
  & > ul {
    @media (min-width: $desktop-sm-bp) {
      height: 220px;
      overflow: auto;
    }
    @media (min-width: $desktop-md-bp) {
      height: 470px;
      overflow: auto;
    }

    @media (min-width: 1366px) and (min-height: 1024px) {
      height: 100%;
      overflow: unset;
    }

    & > li {
      display: flex;
      margin-right: 10px;
      margin-bottom: 1em;
    }
  }
  .ingredient-name {
    @include text($font-size: calc(10px + 0.5vw), $font-weight: 600);
  }
  .dots {
    margin: 0 33px;
    flex-grow: 1;
    border-bottom: 3px dotted #afbdc5;
    height: 0;
    align-self: flex-end;
  }
  .ingredient-number {
    @include text($font-size: calc(10px + 0.5vw));
  }
}

sources.scss\\helpers\\mixins.scss:
@import "../helpers/variables";
@mixin text(
  $color: $primary-text-color,
  $font-family: $primary-font-stack,
  $font-size: "",
  $font-weight: 400
) {
  color: $color;
  font-family: $font-family;
  font-size: $font-size;
  font-weight: $font-weight;
}

@mixin text-clip() {
  background-clip: text;
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
}

/// Mixin to place items on a circle
/// @author Hugo Giraudel
/// @author Ana Tudor
/// @param {Integer} $item-count - Number of items on the circle
/// @param {Length} $circle-size - Large circle size
/// @param {Length} $item-size - Single item size
@mixin on-circle($item-count, $circle-size, $item-size) {
  position: relative;
  width: $circle-size;
  height: $circle-size;
  padding: 0;
  border-radius: 50%;
  list-style: none;

  > * {
    display: block;
    position: absolute;
    top: 50%;
    left: 50%;
    width: $item-size;
    height: $item-size;
    margin: -($item-size / 4);

    $angle: (360 / $item-count);
    $rot: 0;

    @for $i from 1 through $item-count {
      &:nth-of-type(#{$i}) {
        transform: rotate($rot * 1deg)
          translate($circle-size / 2)
          rotate($rot * -1deg);
      }

      $rot: $rot + $angle;
    }
  }
}

