@import "../helpers/mixins.scss";

.preloader-screen {
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background-color: $primary-bg-color;
  z-index: 999;
  visibility: visible;
  &.done {
    visibility: hidden;
  }

  .lds-roller {
    @include on-circle($item-count: 5, $circle-size: 6em, $item-size: 5em);
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);

    & div {
      width: 40px;
      height: 40px;
      background-size: 100%;
      background-position: center;
      background-repeat: no-repeat;

      &:first-child {
        width: 50px;
        height: 50px;
        background-image: url("../images/main-screen/raspberrie.png");
      }
      &:nth-child(2) {
        background-image: url("../images/main-screen/blackberry.png");
      }
      &:nth-child(3) {
        background-image: url("../images/main-screen/strawberry.png");
      }
      &:last-child {
        background-image: url("../images/main-screen/lemon.png");
      }
    }
  }
}

sources.scss\\components\\main-screen.scss:
@import "./helpers/variables";
@import "./helpers/mixins";
.pg-wrapper {
  height: 100% !important;
}

.main-screen {
  background-image: url("../images/main-screen/background.png");
  height: 100vh !important;
  background-repeat: no-repeat;
  background-size: cover;
  background-position: center;
  position: relative;

  @media (min-width: $landscape-bp) and (max-width: 1023px) {
    height: 160vh !important;
  }

  @media (max-width: 1023px) and (min-height: 510px) {
    height: 90vh !important;
  }

  @media (min-width: 768px) and (min-height: 1366px) {
    height: 60vh !important;
  }

  @media (min-width: 1366px) and(min-height: 1024px) {
    height: 80vh !important;
  }
}

.main-screen__burger {
  width: 30px;
  cursor: pointer;
  position: absolute;
  top: 50px;
  right: 50px;
  z-index: 22;
  transition: 0.2s linear;
  @media (min-width: $laptop-sm-bp) {
    width: 40px;
    top: 100px;
    right: 100px;
  }

  &:hover li:nth-child(2),
  &:hover li:last-child {
    width: 100%;
  }

  li {
    transition: 0.2s linear;
    width: 100%;
    height: 4px;
    background-color: #000;

    &:not(:last-child) {
      margin-bottom: 4px;
    }
  }
}

.women-picture {
  width: 66%;
  position: absolute;
  z-index: 5;
  top: 0;
  right: 50%;
  transform: translate(50%, 1%);
  display: none;

  &.hidden {
    z-index: -1;
  }

  & > a {
    position: absolute;
    top: 25%;
    left: 22%;
    width: 20%;
    height: 20%;
    cursor: pointer;
    transition: 0.2s linear;
    &:hover {
      transform: scale(1.1);
    }
  }
  @media (min-width: $laptop-sm-bp) {
    display: block;
    width: 30%;
  }
  @media (min-width: $landscape-bp) and (max-width: $laptop-sm-bp) {
    top: 10px;
  }
}

.main-screen__title-wrapper {
  max-width: 65%;
  width: 100%;
  text-align: center;
  position: absolute;
  top: 15%;
  left: 50%;
  transform: translateX(-50%);
  @media (min-width: $laptop-sm-bp) {
    top: 20%;
    height: 145px;
  }
  @media (min-width: $desktop-lg-bp) {
  }
}

.main-screen__title {
  margin: 0 auto;
  margin-bottom: 1em;
  @media (min-width: $laptop-sm-bp) {
    margin-bottom: 1.5em;
  }

  @media (min-width: $landscape-bp) and (max-width: $laptop-sm-bp) {
    max-width: 90%;
  }
}

.main-screen__subtitle {
  @include text($font-size: 15px);
  letter-spacing: 6px;

  @media (min-width: $landscape-bp) {
    font-size: 20px;
  }

  @media (min-width: $laptop-sm-bp) {
    font-size: 23px;
    letter-spacing: 14px;
    text-align: center;
    cursor: default;
  }
}

.main-screen__decor-mobile {
  position: absolute;
  bottom: 0;
  left: 50%;
  z-index: 20;
  transform: translateX(-50%);
  background-image: url("../images/main-screen/decor-bottom-mobile.png");
  background-repeat: no-repeat;
  background-size: contain;
  background-position: bottom;
  width: 90%;
  height: 50%;

  @media (min-width: $laptop-sm-bp) {
    display: none;
  }

  @media (max-height: 510px) {
    width: 75%;
  }

  @media (min-width: $landscape-bp) {
    width: 45%;
  }

  & > a {
    display: inline-block;
    position: absolute;
    bottom: 0;
    left: 0;
    width: 100%;
    height: 75%;
    & > span {
      position: absolute;
      width: 100%;
      text-align: center;
      left: 50%;
      transform: translateX(-50%);
      bottom: 15px;
      @include text($color: #1c70b0, $font-size: calc(10px + 0.5vw));
      text-transform: uppercase;

      @media (max-height: 510px) {
        font-size: calc(9px + 0.5vw);
      }
    }

    & > .arrow-wrapper {
      width: 100%;
      height: 100%;
      position: absolute;
      bottom: 0;
      left: 0;
      & > img {
        transform: rotate(90deg) translateY(50%);
        position: absolute;
        bottom: 37px;
        left: 50%;
        max-width: 10%;
      }
    }
  }
}

sources.scss\\components\\product-screen.scss:
@import "./helpers/variables";
@import "./helpers/mixins";

//Using "!important" only for overwriting Parallax.js positions styles!

.product-screen {
  position: relative;

  & > .vines {
    opacity: 1;
    width: 100%;
    position: absolute;
    top: 0;
    left: 0;
    transform: translateY(-30%);
    z-index: -1;
    visibility: visible;
    &.hidden {
      opacity: 0;
      visibility: hidden;
    }
  }
  &__parrot {
    max-width: 20%;
    width: 125px;
    height: 125px;
    position: absolute;
    top: 30% !important;
    left: 65% !important;

    @media (min-width: $desktop-sm-bp) {
      top: 20% !important;
      left: 12% !important;
    }

    @media (min-width: 1024px) and (min-height: 768px) {
      top: 165px !important;
      left: 10% !important;
    }
  }
  &__vitamins {
    z-index: 2;
    width: 100%;
    height: 100%;
    margin-top: 3em;

    @media (min-width: $tablet-bp) {
      position: absolute;
      margin: 0;
      top: 60px !important;
      left: 34% !important;
    }

    @media (min-width: $desktop-sm-bp) {
      top: 21% !important;
      left: 34% !important;
    }

    & > img {
      max-width: 59%;
    }

    &--lg {
      width: 95%;

      @media (min-width: $tablet-bp) {
        top: 110px !important;
        left: 8% !important;
      }

      @media (min-width: $desktop-sm-bp) {
        max-width: 100%;
        top: 28% !important;
        left: 10% !important;
      }

      & > img {
        max-width: 100%;
        @media (min-width: $tablet-bp) {
          max-width: 90%;
        }
      }
    }
  }

  &__plants {
    z-index: -1;
    max-width: 200%;
    position: absolute;
    top: 20%;
    left: -10%;

    @media (min-width: $desktop-sm-bp) {
      top: 10% !important;
      left: -10% !important;
    }

    @media (min-width: $tablet-bp) {
      top: 5%;
    }

    & > picture > img {
      @media (max-width: 767px) {
        width: 350px;
      }
    }
  }

  &__reef {
    max-width: 100%;
    z-index: -1;
    position: absolute;
    top: -10%;
    left: -35%;

    @media (min-width: $desktop-sm-bp) {
      top: 0% !important;
      left: -10% !important;
    }

    @media (min-width: 1024px) and (min-height: 768px) {
      top: -45px;
      left: -10%;
    }

    @media (min-width: 768px) and (min-height: 1024px) {
      top: -45px;
      left: -10%;
    }

    & > img {
      @media (max-width: $desktop-sm-bp) {
        width: 100%;
      }
    }
  }

  &__fish {
    @extend .product-screen__parrot;
    left: 70% !important;
    top: 10% !important;
    @media (min-width: $desktop-sm-bp) {
      top: 2% !important;
    }
    @media (min-width: 1024px) and (min-height: 768px) {
      top: 30px !important;
      left: 77% !important;
    }
    @media (min-width: 768px) and (min-height: 1024px) {
      top: 30px !important;
      left: 77% !important;
    }
  }

  &__night {
    @extend .product-screen__reef;
    width: 100%;
    z-index: -1;
    top: 0%;
    left: -10%;

    @media (min-width: $tablet-bp) {
      left: 20%;
    }

    @media (min-width: $desktop-sm-bp) {
      max-width: 81%;
      top: 0% !important;
      left: 16% !important;
    }

    & > img {
      @media (max-width: $desktop-sm-bp) {
        width: 75%;
      }
    }
  }

  &__owl {
    top: 10%;
    left: 60%;
    position: absolute;
    width: 65px;
    height: 65px;

    @media (min-width: $tablet-bp) {
      top: 10% !important;
    }

    @media (min-width: $desktop-sm-bp) {
      width: 90px;
      height: 90px;
      top: 20% !important;
      left: 80% !important;
    }

    @media (min-width: 1024px) and (min-height: 768px) {
      top: 30px !important;
      left: 77% !important;
    }
  }
  &__forest {
    @extend .product-screen__reef;
    max-width: 93%;
    left: -1% !important;
    top: -1% !important;

    @media (min-wdith: $tablet-bp) {
      max-width: 93%;
    }

    @media (min-wdith: $desktop-sm-bp) {
      left: -17% !important;
      top: -16% !important;
    }

    @media (min-width: $desktop-lg-bp) {
      // left: -11% !important;
      // top: -13% !important;
    }

    & > img {
      width: 100%;
    }
  }
  &__bug {
    @extend .product-screen__owl;
  }
  &__planet {
    border-radius: 50%;
    overflow: hidden;
    position: absolute !important;
    top: 23%;
    transform: translate(0, 140%) !important;
    display: none;

    @media (min-width: $tablet-bp) {
      top: 25%;
      left: -9%;
      display: block;
    }

    @media (min-width: $desktop-sm-bp) {
      top: 10% !important;
      left: 0 !important;
    }

    @media (min-width: $desktop-lg-bp) {
      top: 17% !important;
    }

    @media (min-width: 1024px) and (min-height: 768px) {
      top: 10%;
      left: 4%;
    }

    & > div {
      width: 10vw;
      height: 10vw;
      background-image: url("../images/products-screens/omega/planet.svg");
      background-repeat: no-repeat;
      background-size: 200% 200%;
      animation: planetRotation 5s linear infinite alternate-reverse;
      transition: transform 200ms linear;

      @media (min-width: $tablet-bp) {
        width: 9vw;
        height: 9vw;
      }

      @media (min-width: $desktop-lg-bp) {
        width: 7.5vw;
        height: 7.5vw;
      }
    }
  }
  &__spaceship {
    @extend .product-screen__owl;
    top: 5% !important;
    left: 60% !important;
    width: 80px;
    @media (min-width: $desktop-sm-bp) {
      width: 120px;
    }
    @media (min-width: 1024px) and (min-height: 768px) {
      top: 30px !important;
      left: 77% !important;
    }
    @media (min-width: 768px) and (min-height: 1024px) {
      top: 30px !important;
      left: 77% !important;
    }
  }

  &__space {
    @extend .product-screen__reef;
    max-width: 100%;
    top: 3%;
    left: -30%;

    @media (min-width: $tablet-bp) {
      top: 5%;
      left: 0%;
    }

    @media (min-width: $desktop-sm-bp) {
      top: 9% !important;
      left: 0% !important;
    }

    & > img {
      @media (max-width: $desktop-sm-bp) {
        width: 100%;
      }
    }
  }

  &__ballons {
    max-width: 25%;
    width: 190px;
    position: absolute !important;
    top: 0;
    z-index: -1;
    left: 0%;

    @media (min-width: $desktop-sm-bp) {
      top: 5% !important;
      left: 10% !important;
    }
  }

  &__city {
    @extend .product-screen__reef;
    top: 0;
    left: 5%;
    width: 80%;

    @media (min-width: 768px) and (min-height: 1024px) {
      top: 50px;
    }

    @media (min-width: $desktop-sm-bp) {
      top: 10% !important;
      left: 5% !important;
    }
  }
}

@keyframes planetRotation {
  from {
    background-position: 85% 20%;
  }
  to {
    background-position: 10% 80%;
  }
}

.product-screen__content {
  height: 100%;
  display: flex;
  flex-direction: column;

  @media (max-width: 1023px) {
    overflow: hidden;
    padding: 0 0 2em;
  }
  @media (min-width: $landscape-bp) {
    flex-direction: row;
  }
}

.product-screen__pictures {
  user-select: none;
  opacity: 1;
  position: relative;
  left: 1%;
  top: 10%;
  width: 100%;
  height: 100%;
  transition: 0.5s all;
  margin-bottom: 3em;
  &.hidden {
    opacity: 0;
  }

  @media (min-width: $desktop-sm-bp) {
    margin: 0;
    top: 0 !important;
    left: 0 !important;
  }
}

.product-screen__desc {
  width: 100%;
  z-index: 2;
  padding: 0 1em;
  align-self: center;

  @media (min-width: $landscape-bp) {
    padding: 3em 1em;
  }

  @media (min-width: $tablet-bp) {
    padding: 5em 1em;
  }

  @media (min-width: $laptop-sm-bp) {
    padding: 7em 1em;
  }

  @media (min-with: $desktop-sm-bp) {
    width: 55%;
    padding: 0 0 0 5em;
  }
  @media (min-width: $desktop-md-bp) {
    width: 100%;
  }
}

.product-screen__title {
  font-size: 31px;
  font-family: $primary-font-stack;
  font-weight: 700;
  color: $primary-text-color;
  text-transform: uppercase;
  margin-bottom: 0.4em;

  @media (max-width: 375px) {
    font-size: 25px;
  }

  @media (min-width: $desktop-sm-bp) {
    margin-bottom: 0.6em;
    font-size: 73px;
  }

  &--green {
    background: linear-gradient(
      45deg,
      rgba(62, 62, 62, 1) 18%,
      rgba(47, 119, 34, 1) 55%
    ); /* W3C, IE10+, FF16+, Chrome26+, Opera12+, Safari7+ */
    filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#3e3e3e', endColorstr='#2f7722',GradientType=1 ); /* IE6-9 fallback on horizontal gradient */
    @include text-clip();
  }

  &--purple {
    @extend .product-screen__title;
    background: linear-gradient(
      45deg,
      rgba(23, 25, 68, 1) 27%,
      rgba(186, 129, 226, 1) 58%
    ); /* W3C, IE10+, FF16+, Chrome26+, Opera12+, Safari7+ */
    filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#171944', endColorstr='#ba81e2',GradientType=1 ); /* IE6-9 fallback on horizontal gradient */
    @include text-clip();
  }
  &--blue {
    @extend .product-screen__title;
    background: linear-gradient(
      45deg,
      rgba(30, 44, 84, 1) 37%,
      rgba(28, 109, 201, 1) 68%
    ); /* W3C, IE10+, FF16+, Chrome26+, Opera12+, Safari7+ */
    filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#1e2c54', endColorstr='#1c6dc9',GradientType=1 ); /* IE6-9 fallback on horizontal gradient */
    @include text-clip();
  }
  &--pink {
    @extend .product-screen__title;
    background: linear-gradient(
      45deg,
      rgba(25, 17, 13, 1) 25%,
      rgba(238, 50, 195, 1) 48%
    ); /* W3C, IE10+, FF16+, Chrome26+, Opera12+, Safari7+ */
    filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#19110d', endColorstr='#ee32c3',GradientType=1 ); /* IE6-9 fallback on horizontal gradient */
    @include text-clip();
  }
  &--red {
    background: linear-gradient(
      45deg,
      rgba(35, 13, 1, 1) 22%,
      rgba(218, 31, 0, 1) 58%
    ); /* W3C, IE10+, FF16+, Chrome26+, Opera12+, Safari7+ */
    filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#230d01', endColorstr='#da1f00',GradientType=1 ); /* IE6-9 fallback on horizontal gradient */
    @include text-clip();
  }
}

.product-screen__text {
  width: 100%;
  font-size: calc(11px + 0.5vw);
  line-height: 22px;
  color: #403e3e;
  font-family: $primary-font-stack;
  font-weight: 400;
  margin-bottom: 1.5em;

  @media (max-width: 375px) {
    line-height: 17px;
  }

  @media (min-width: $desktop-sm-bp) {
    width: 600px;
    line-height: 36px;
    margin-bottom: 3.5em;
  }

  @media (min-width: $desktop-lg-bp) {
    font-size: 24px;
  }

  & strong {
    font-weight: 600;
  }
}

.product-screen__btn {
  font-family: $primary-font-stack;
  font-size: 14px;
  font-weight: 800;
  border: 5px solid transparent;
  border-radius: 44px;
  background-color: #ffffff;
  padding: 1em 2.2em 1em 6em;
  position: relative;
  display: inline-block;
  text-transform: uppercase;
  cursor: pointer;
  transition: 0.2s linear;

  @media (min-width: $desktop-sm-bp) {
    font-size: 18px;
    padding: 1.2em 2.2em 1.2em 6em;
  }

  @media (min-width: $desktop-lg-bp) {
    font-size: 22px;
  }

  &::before {
    content: "";
    display: inline-block;
    border: 6px solid $primary-bg-color;
    border-radius: 50%;
    position: absolute;
    background-position: center;
    background-size: cover;
    background-repeat: no-repeat;
    width: 65px;
    height: 65px;
    top: 0;
    left: 0;
    transform: translate(16%, -35%);

    @media (min-width: $desktop-sm-bp) {
      width: 78px;
      height: 78px;
    }

    @media (min-width: $desktop-lg-bp) {
      width: 90px;
      height: 90px;
    }
  }

  &::after {
    content: "";
    display: inline-block;
    background-repeat: no-repeat;
    background-position: center;
    position: absolute;
    top: 50%;
    right: 5%;
    transform: translate(-50%, -40%);
    width: 10px;
    height: 10px;

    @media (min-width: $desktop-sm-bp) {
      width: 18px;
      height: 18px;
    }
  }
  &--green {
    color: $green-btn;
    &:hover {
      border-color: $green-btn;
    }
    &::after {
      background-image: url("data:image/svg+xml,%3Csvg id='Layer_1' xmlns='http://www.w3.org/2000/svg' viewBox='0 0 26.5 43.8'%3E%3Cstyle%3E .st0%7Bfill:%2300a042%7D %3C/style%3E%3Cpath transform='rotate(45.001 13.258 13.257)' class='st0' d='M-1.7 9.5h30V17h-30z'/%3E%3Cpath transform='rotate(-45.001 13.257 30.507)' class='st0' d='M-1.7 26.8h30v7.5h-30z'/%3E%3C/svg%3E");
    }
    &::before {
      background-image: url("../images/products-screens/calcivit/btn-icon.png");
    }
  }
  &--blue {
    &:hover {
      border-color: $blue-btn;
    }
    color: $blue-btn;
    &::after {
      background-image: url("data:image/svg+xml,%3Csvg id='Layer_1' xmlns='http://www.w3.org/2000/svg' viewBox='0 0 26.5 43.8'%3E%3Cstyle%3E .st0%7Bfill:%23006d9e%7D %3C/style%3E%3Cpath transform='rotate(45.001 13.258 13.257)' class='st0' d='M-1.7 9.5h30V17h-30z'/%3E%3Cpath transform='rotate(-45.001 13.257 30.507)' class='st0' d='M-1.7 26.8h30v7.5h-30z'/%3E%3C/svg%3E");
    }
    &::before {
      background-image: url("../images/products-screens/multivit/btn-icon.png");
    }
  }
  &--purple {
    color: $purple-btn;
    &:hover {
      border-color: $purple-btn;
    }
    &::after {
      background-image: url("data:image/svg+xml,%3Csvg id='Layer_1' xmlns='http://www.w3.org/2000/svg' viewBox='0 0 26.5 43.8'%3E%3Cstyle%3E .st0%7Bfill:%239e1679%7D %3C/style%3E%3Cpath transform='rotate(45.001 13.258 13.257)' class='st0' d='M-1.7 9.5h30V17h-30z'/%3E%3Cpath transform='rotate(-45.001 13.257 30.507)' class='st0' d='M-1.7 26.8h30v7.5h-30z'/%3E%3C/svg%3E");
    }
    &::before {
      background-image: url("../images/products-screens/immunovit/btn-icon.png");
    }
  }
  &--orange {
    color: $orange-btn;
    &:hover {
      border-color: $orange-btn;
    }
    &::after {
      background-image: url("data:image/svg+xml,%3Csvg id='Layer_1' xmlns='http://www.w3.org/2000/svg' viewBox='0 0 26.5 43.8'%3E%3Cstyle%3E .st0%7Bfill:%23de3c16%7D %3C/style%3E%3Cpath transform='rotate(45.001 13.258 13.257)' class='st0' d='M-1.7 9.5h30V17h-30z'/%3E%3Cpath transform='rotate(-45.001 13.257 30.507)' class='st0' d='M-1.7 26.8h30v7.5h-30z'/%3E%3C/svg%3E");
    }
    &::before {
      background-image: url("../images/products-screens/smartvit/btn-icon.png");
    }
  }
  &--pink {
    color: $pink-btn;
    &:hover {
      border-color: $pink-btn;
    }
    &::after {
