@import "./helpers/variables";

.about-screen-wrapper {
  background-image: url("../images/about-products/background.jpg");
  background-repeat: no-repeat;
  background-position: center;
  background-size: cover;

  @media (min-width: 768px) and (min-height: 768px) {
    background-image: url("../images/about-products/bg-mobile.png");
  }
}

.about-screen__content {
  width: 100%;
  height: 100%;
  display: flex;
  align-items: center;
  justify-content: center;
}

.about-screen__desc {
  width: 100%;
  text-align: center;
  margin-top: 5em;
  padding: 0 1em;
  visibility: visible;
  z-index: 2;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;

  @media (max-width: 375px) {
    margin-top: 2em;
  }

  @media (min-width: $desktop-sm-bp) {
    padding: 0;
    width: 870px;
    height: 100%;
  }

  &.hidden {
    opacity: 0;
    visibility: hidden;
  }
}

.about-screen__text {
  font-family: $primary-font-stack;
  color: $primary-text-color;
  line-height: 34px;
  font-weight: 400;
  text-align: center;
  font-size: calc(11px + 0.5vw);
  margin-bottom: 52px;

  @media (max-width: 375px) {
    line-height: 19px;
    margin-bottom: 25px;
  }

  @media (min-width: $desktop-sm-bp) {
    font-size: calc(12px + 0.5vw);
    line-height: 35px;
  }

  @media (min-width: 1024px) and (min-height: 1366px) {
    line-height: 35px;
  }

  & > span {
    font-size: calc(11px + 0.5vw);
    font-family: $primary-font-stack;
    color: $primary-text-color;
    position: relative;

    @media (min-width: $desktop-sm-bp) {
      font-size: calc(12px + 0.5vw);
    }

    &:after {
      content: "\00AE";
      display: inline-block;
      position: absolute;
      top: -7px;
      right: -8px;
      width: 6px;
      height: 6px;

      @media (min-width: 768px) and (min-height: 1024px) {
        top: -15px;
        right: -7px;
      }

      @media (min-width: 1024px) and (min-height: 768px) {
        top: -15px;
        right: -7px;
      }
    }
  }
}

.about-screen__title {
  @extend .about-screen__text;
  font-weight: 700;
  line-height: 38px;
  font-size: calc(30px + 0.5vw);
  text-transform: uppercase;
  background: linear-gradient(
    45deg,
    rgba(31, 45, 55, 1) 27%,
    rgba(78, 126, 159, 1) 60%
  ); /* W3C, IE10+, FF16+, Chrome26+, Opera12+, Safari7+ */
  filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#1f2d37', endColorstr='#4e7e9f',GradientType=1 ); /* IE6-9 fallback on horizontal gradient */
  background-clip: text;
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  margin-bottom: 28px;

  @media (max-width: 375px) {
    font-size: calc(26px + 0.5vw);
  }

  @media (min-width: $desktop-sm-bp) {
    line-height: 70px;
    font-size: calc(40px + 0.5vw);
    margin-bottom: 52px;
  }

  @media (min-width: $desktop-md-bp) {
    font-size: calc(50px + 0.5vw);
  }
}

.about-screen__btn {
  @extend .about-screen__title;
  display: inline-block;
  font-size: calc(15px + 0.5vw);
  line-height: unset;
  border: 3px solid $primary-text-color;
  border-radius: 64px;
  padding: 15px 39px;
  position: relative;
  cursor: pointer;

  @media (min-width: $desktop-sm-bp) {
    font-size: calc(17px + 0.5vw);
    padding: 20px 110px;
  }

  @media (min-width: $desktop-lg-bp) {
    font-size: calc(20px + 0.5vw);
    padding: 35px 130px;
  }

  &::after {
    content: "";
    display: inline-block;
    background-image: url("data:image/svg+xml,%3Csvg id='Layer_1' xmlns='http://www.w3.org/2000/svg' viewBox='0 0 26.5 43.8'%3E%3Cstyle%3E .st0%7Bfill:%23403e3e%7D %3C/style%3E%3Cpath transform='rotate(45.001 13.258 13.257)' class='st0' d='M-1.7 9.5h30V17h-30z'/%3E%3Cpath transform='rotate(-45.001 13.257 30.507)' class='st0' d='M-1.7 26.8h30v7.5h-30z'/%3E%3C/svg%3E");
    background-repeat: no-repeat;
    background-position: center;
    position: absolute;
    align-self: center;
    top: 48%;
    right: 6%;
    transform: translate(-50%, -40%);
    width: 14px;
    height: 14px;

    @media (min-width: $desktop-sm-bp) {
      width: 20px;
      height: 20px;
      right: 12%;
    }

    @media (min-width: $desktop-lg-bp) {
      top: 49%;
      transform: translate(-50%, -40%);
      width: 30px;
      height: 30px;
    }
  }
}

sources.scss\\components\\up-button.scss:
@import "../helpers/variables";
.up-button {
  display: none;
  position: fixed;
  right: 4%;
  bottom: 8%;
  width: 60px;
  height: 60px;
  z-index: 999;
  cursor: pointer;
  user-select: none;
  background-image: url("data:image/svg+xml,%3Csvg data-name='Layer 1' xmlns='http://www.w3.org/2000/svg' viewBox='0 0 256 256'%3E%3Cdefs%3E%3ClinearGradient id='a' y1='128' x2='256' y2='128' gradientUnits='userSpaceOnUse'%3E%3Cstop offset='0' stop-color='%23ca5424'/%3E%3Cstop offset='.26' stop-color='%23966395'/%3E%3Cstop offset='.51' stop-color='%23458fa0'/%3E%3Cstop offset='1' stop-color='%2345a575'/%3E%3C/linearGradient%3E%3C/defs%3E%3Cpath d='M128 0a128 128 0 1 0 128 128A128 128 0 0 0 128 0z' fill='url(%23a)'/%3E%3Cpath fill='%23fff' d='M186.32 141.73L128 83.4l-58.32 58.33 18.87 18.87L128 121.14l39.45 39.46 18.87-18.87z'/%3E%3C/svg%3E");

  @media (min-width: $desktop-sm-bp) {
    display: block;
  }

  &:hover {
    animation: bounce 0.7s;
  }
}

@keyframes bounce {
  0%,
  20%,
  60%,
  100% {
    -webkit-transform: translateY(0);
    transform: translateY(0);
  }

  40% {
    -webkit-transform: translateY(-20px);
    transform: translateY(-20px);
  }

  80% {
    -webkit-transform: translateY(-10px);
    transform: translateY(-10px);
  }
}

sources.scss\\components\\products-list.scss:
@import "./main-page.scss";
@import "../helpers/variables";
@import "../helpers/mixins";
@import "../components/product-screen";

.products-list {
  @extend .main-page__screen;
  height: 100vh;

  @media (min-width: 1366px) and (min-height: 1024px) {
    height: 100% !important;
  }

  @media (min-width: 1024px) and (min-height: 1366px) {
    height: 100% !important;
  }
}

.products-list__inner {
  padding: 0.7em 0.5em;
  border: 10px solid #fff;
  position: relative;
  height: 100%;
  display: flex;
  flex-direction: column;
  justify-content: center;
  @media (min-width: $desktop-sm-bp) {
    padding: 3.5em 7.5em;
    border: 20px solid #fff;
  }
}

.products-list__content {
  display: flex;
  align-items: center;
  align-self: center;
  width: 100%;
  height: 100%;
  justify-content: space-between;
}

.products-list__item {
  background-color: #fff;
  flex-basis: calc(33.33% - 10em);
  display: flex;
  width: 100%;
  height: 100%;
  padding: 4.5em 0;
  flex-direction: column;
  justify-content: center;

  @media (min-width: $desktop-sm-bp) {
    padding: 2em 0;
    margin-right: 1.2em;
  }
  @media (min-width: $desktop-lg-bp) {
    padding: 4.5em 0;
  }

  & > a {
    width: 100%;
    height: 100%;

    img {
      width: 75%;
      margin: 0 auto;
      margin-bottom: 4em;

      @media (min-width: $desktop-sm-bp) {
        margin-bottom: 2em;
      }

      @media (min-width: $desktop-lg-bp) {
        margin-bottom: 4em;
      }
    }
  }

  .btn-wrapper {
    text-align: center;
  }
}

.product-list__title {
  @include text($font-size: calc(18px + 0.5vw), $font-weight: 700);
  text-align: center;
  padding: 0 0 2em;

  @media (min-width: $desktop-sm-bp) and (max-width: $desktop-md-bp) {
    font-size: 18px;
  }
}

.products-list__btn {
  @extend .product-screen__btn;
  border: 3px solid;

  @media (min-width: $desktop-sm-bp) and (max-width: $desktop-md-bp) {
    font-size: 12px;
  }

  &::before {
    border-color: #fff;
    @media (min-width: $desktop-sm-bp) and (max-width: $desktop-md-bp) {
      width: 55px;
      height: 55px;
    }
  }

  &::after {
    @media (min-width: $desktop-sm-bp) and (max-width: $desktop-md-bp) {
      width: 10px;
      height: 10px;
    }
  }

  &:hover span {
    color: #fff;
  }
  &:hover:after {
    background-image: url("data:image/svg+xml,%3Csvg id='Layer_1' xmlns='http://www.w3.org/2000/svg' viewBox='0 0 26.5 43.8'%3E%3Cstyle%3E .st0%7Bfill:%23fff%7D %3C/style%3E%3Cpath transform='rotate(45.001 13.258 13.257)' class='st0' d='M-1.7 9.5h30V17h-30z'/%3E%3Cpath transform='rotate(-45.001 13.257 30.507)' class='st0' d='M-1.7 26.8h30v7.5h-30z'/%3E%3C/svg%3E");
  }

  &--green {
    @extend .product-screen__btn--green;
    &:hover {
      background-color: $green-btn;
    }
  }
  &--blue {
    @extend .product-screen__btn--blue;
    &:hover {
      background-color: $blue-btn;
    }
  }
  &--purple {
    @extend .product-screen__btn--purple;
    &:hover {
      background-color: $purple-btn;
    }
  }
  &--orange {
    @extend .product-screen__btn--orange;
    &:hover {
      background-color: $orange-btn;
    }
  }
  &--pink {
    @extend .product-screen__btn--pink;
    &:hover {
      background-color: $pink-btn;
    }
  }
  &--purple {
    @extend .product-screen__btn--purple;
    &:hover {
      background-color: $purple-btn;
    }
  }
}

