sources.scss\\components\\product-page.scss:
@import "./main-page.scss";
@import "../helpers/variables";
@import "../components/slider-control";
.product-page__main-screen {
  @extend .main-page__screen;

  @media (max-width: 567px) {
    height: 100vh;
  }

  @media (min-width: 768px) and (min-height: 1366px) {
    height: 60vh !important;
  }

  @media (min-width: 1366px) and(min-height: 1024px) {
    height: 70vh !important;
  }
}

.product-page__inner {
  @extend .main-page__inner;
  background-image: url("../images/product-page/light-481.png");
  width: 100%;
  background-repeat: no-repeat;
  background-position: center;
  background-size: contain;
  @media (min-width: 375px) and (max-width: 400px),
    (min-width: 468px),
    (-webkit-min-device-pixel-ratio: 2),
    (min-resolution: 192dpi) {
    background-image: url("../images/product-page/light.png");
  }
}

.product-page__title-wrapper {
  text-align: center;
  margin: 5em 0 2em;

  @media (min-width: $desktop-sm-bp) {
    margin: 2em 0 2.5em;
  }
}

.product-page__title {
  @extend .main-screen__title;
  align-self: center;
  font-weight: 700;
  text-transform: uppercase;
  font-family: $primary-font-stack;
  font-size: calc(30px + 0.5vw);
  margin-bottom: 30px;
  background: rgb(61, 165, 107); /* Old browsers */

  background: linear-gradient(
    135deg,
    rgba(61, 165, 107, 1) 17%,
    rgba(61, 165, 107, 1) 36%,
    rgba(69, 125, 171, 1) 48%,
    rgba(171, 79, 135, 1) 52%,
    rgba(223, 67, 7, 1) 60%,
    rgba(223, 67, 7, 1) 100%
  ); /* W3C, IE10+, FF16+, Chrome26+, Opera12+, Safari7+ */
  filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#3da56b', endColorstr='#df4307',GradientType=1 ); /* IE6-9 fallback on horizontal gradient */
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;

  @media (min-width: $desktop-sm-bp) {
    font-size: calc(40px + 1vw);
  }

  @media (min-width: $desktop-lg-bp) {
    font-size: calc(75px + 1vw);
  }
}

.product-page__vitamins {
  margin: 0 auto;
  display: flex;
  align-items: center;
  max-width: 55%;

  @media (max-width: 567px) {
    max-width: 85%;
  }

  @media (min-width: $desktop-sm-bp) {
    max-width: 31%;
    align-items: flex-end;
  }

  @media (min-width: $desktop-lg-bp) {
    max-width: 38%;
  }
}

.product-page__subtitle {
  @extend .main-screen__subtitle;
  text-transform: uppercase;
  top: 0;
  font-size: 11px;
  letter-spacing: 2px;
  @media (min-width: $desktop-sm-bp) {
    font-size: 23px;
  }
}

.product-page__about {
  @extend .main-page__screen;
  @media (min-width: 1024px) and(min-height: 1366px) {
    height: 100%;
  }
}

.product-page__slider-control {
  display: flex;
  align-items: center;
  justify-content: space-between;
  position: absolute;
  pointer-events: none;
  top: 50%;
  left: 0;
  transform: translateY(-50%);
  width: 100%;
  padding: 0 2em;
  z-index: 20;

  & > button {
    height: 50px;
    width: 50px;
    cursor: pointer;
    color: #acacac;
    transition: 0.1s linear;
    pointer-events: auto;

    &:hover {
      color: #464646;
    }
  }
}

.product-page__fruits {
  @extend .main-page__fruits;
  height: 100%;

  & > ul:first-child {
    top: -20% !important;
    left: 10%;
    width: 50%;
    height: 100%;
    li {
      position: absolute !important;
    }
  }
  & > ul:last-child {
    right: -10%;
    width: 50%;
    height: 100%;
    li {
      position: absolute !important;
    }
  }
}

.smartvit-fruits {
  & > ul:first-child {
    li {
      &:first-child {
        max-width: 40%;
        top: -35% !important;
      }
      &:last-child {
        max-width: 20%;
        top: 10% !important;
        left: 40% !important;
      }
    }
  }
  & > ul:last-child {
    li {
      position: absolute !important;
      top: 0;
      &:first-child {
        max-width: 20%;
        top: -45% !important;
      }
      &:last-child {
        max-width: 40%;
        top: -20% !important;
      }
    }
  }
}

.calcivit-fruits {
  & > ul:first-child {
    li {
      &:first-child {
        max-width: 30%;
        top: -30% !important;
        left: -20% !important;
      }
      &:nth-child(2) {
        top: 5% !important;
        max-width: 6%;
      }
      &:nth-child(3) {
        top: 10% !important;
        left: 40% !important;
      }
      &:last-child {
        max-width: 9%;
        top: -25% !important;
        left: 45% !important;
      }
    }
  }
  & > ul:last-child {
    li {
      position: absolute !important;
      top: 0;
      &:first-child {
        max-width: 20%;
        top: -60% !important;
      }
      &:nth-child(2) {
        top: -47% !important;
        left: -19% !important;
        max-width: 6%;
      }
      &:nth-child(3) {
        top: -30% !important;
        max-width: 17%;
      }
      &:last-child {
        max-width: 7%;
        top: 0% !important;
        left: 30% !important;
      }
    }
  }
}

.multivit-fruits {
  @extend .calcivit-fruits;
  & > ul:last-child {
    li {
      &:nth-child(3) {
        top: -30% !important;
        max-width: 30%;
      }
    }
  }
}

.hello-kitty-fruits {
  @extend .calcivit-fruits;

  & > ul:first-child {
    li {
      &:first-child {
        max-width: 17%;
        top: -30% !important;
        left: -3% !important;
      }

      &:nth-child(2) {
        top: 5% !important;
        max-width: 23%;
      }

      &:last-child {
        max-width: 9%;
        top: -4% !important;
        left: 30% !important;
      }
    }
  }

  & > ul:last-child {
    li {
      &:first-child {
        max-width: 23%;
        top: -60% !important;
        left: 22% !important;
      }

      &:nth-child(2) {
        top: -35% !important;
        left: 0% !important;
        max-width: 7%;
      }

      &:last-child {
        max-width: 20%;
        top: 0% !important;
        left: 15% !important;
      }
    }
  }
}

sources.scss\\components\\back-btn.scss:
