@import "./helpers/variables";
@import "./helpers/mixins";
.pg-wrapper {
  height: 100% !important;
}

.main-screen {
  background-image: url("../images/main-screen/background.png");
  height: 100vh !important;
  background-repeat: no-repeat;
  background-size: cover;
  background-position: center;
  position: relative;

  @media (min-width: $landscape-bp) and (max-width: 1023px) {
    height: 160vh !important;
  }

  @media (max-width: 1023px) and (min-height: 510px) {
    height: 90vh !important;
  }

  @media (min-width: 768px) and (min-height: 1366px) {
    height: 60vh !important;
  }

  @media (min-width: 1366px) and(min-height: 1024px) {
    height: 80vh !important;
  }
}

.main-screen__burger {
  width: 30px;
  cursor: pointer;
  position: absolute;
  top: 50px;
  right: 50px;
  z-index: 22;
  transition: 0.2s linear;
  @media (min-width: $laptop-sm-bp) {
    width: 40px;
    top: 100px;
    right: 100px;
  }

  &:hover li:nth-child(2),
  &:hover li:last-child {
    width: 100%;
  }

  li {
    transition: 0.2s linear;
    width: 100%;
    height: 4px;
    background-color: #000;

    &:not(:last-child) {
      margin-bottom: 4px;
    }
  }
}

.women-picture {
  width: 66%;
  position: absolute;
  z-index: 5;
  top: 0;
  right: 50%;
  transform: translate(50%, 1%);
  display: none;

  &.hidden {
    z-index: -1;
  }

  & > a {
    position: absolute;
    top: 25%;
    left: 22%;
    width: 20%;
    height: 20%;
    cursor: pointer;
    transition: 0.2s linear;
    &:hover {
      transform: scale(1.1);
    }
  }
  @media (min-width: $laptop-sm-bp) {
    display: block;
    width: 30%;
  }
  @media (min-width: $landscape-bp) and (max-width: $laptop-sm-bp) {
    top: 10px;
  }
}

.main-screen__title-wrapper {
  max-width: 65%;
  width: 100%;
  text-align: center;
  position: absolute;
  top: 15%;
  left: 50%;
  transform: translateX(-50%);
  @media (min-width: $laptop-sm-bp) {
    top: 20%;
    height: 145px;
  }
  @media (min-width: $desktop-lg-bp) {
  }
}

.main-screen__title {
  margin: 0 auto;
  margin-bottom: 1em;
  @media (min-width: $laptop-sm-bp) {
    margin-bottom: 1.5em;
  }

  @media (min-width: $landscape-bp) and (max-width: $laptop-sm-bp) {
    max-width: 90%;
  }
}

.main-screen__subtitle {
  @include text($font-size: 15px);
  letter-spacing: 6px;

  @media (min-width: $landscape-bp) {
    font-size: 20px;
  }

  @media (min-width: $laptop-sm-bp) {
    font-size: 23px;
    letter-spacing: 14px;
    text-align: center;
    cursor: default;
  }
}

.main-screen__decor-mobile {
  position: absolute;
  bottom: 0;
  left: 50%;
  z-index: 20;
  transform: translateX(-50%);
  background-image: url("../images/main-screen/decor-bottom-mobile.png");
  background-repeat: no-repeat;
  background-size: contain;
  background-position: bottom;
  width: 90%;
  height: 50%;

  @media (min-width: $laptop-sm-bp) {
    display: none;
  }

  @media (max-height: 510px) {
    width: 75%;
  }

  @media (min-width: $landscape-bp) {
    width: 45%;
  }

  & > a {
    display: inline-block;
    position: absolute;
    bottom: 0;
    left: 0;
    width: 100%;
    height: 75%;
    & > span {
      position: absolute;
      width: 100%;
      text-align: center;
      left: 50%;
      transform: translateX(-50%);
      bottom: 15px;
      @include text($color: #1c70b0, $font-size: calc(10px + 0.5vw));
      text-transform: uppercase;

      @media (max-height: 510px) {
        font-size: calc(9px + 0.5vw);
      }
    }

    & > .arrow-wrapper {
      width: 100%;
      height: 100%;
      position: absolute;
      bottom: 0;
      left: 0;
      & > img {
        transform: rotate(90deg) translateY(50%);
        position: absolute;
        bottom: 37px;
        left: 50%;
        max-width: 10%;
      }
    }
  }
}

