    }
    li {
      position: unset !important;

      &:first-child {
        max-width: 12%;
        margin-left: 12em;
      }
      &:nth-child(2) {
        max-width: 25%;
        margin-left: 7em;
      }
      &:nth-child(3) {
        max-width: 40%;
        margin-left: 5em;
      }
      &:nth-child(4) {
        max-width: 85%;
      }
      @media (min-width: $tablet-bp) {
        &:first-child {
          margin-left: 12em;
        }
