@import "./main-page.scss";
@import "../helpers/variables";
@import "../helpers/mixins";

.comments {
  width: 100%;
  height: unset;
  background-color: #fff;
  margin: 5em auto;
  padding: 1.2em;
  z-index: 5;
  position: relative;

  @media (min-width: $laptop-sm-bp) {
    margin-top: 10em;
  }

  @media (min-width: $desktop-sm-bp) {
    width: 70%;
    overflow: auto;
    max-height: 72vh;
    margin-top: 2em;
  }

  @media (min-width: $desktop-lg-bp) {
    margin-top: 5em;
  }

  @media (min-width: 1366px) and(min-height: 1024px) {
    margin-top: 5em;
  }
}

.comments__list {
  @media (min-width: $desktop-sm-bp) {
    padding: 2em 5em;
  }

  & > li {
    width: 100%;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    margin-bottom: 40px;

    @media (min-width: $desktop-sm-bp) {
      margin-bottom: 96px;
    }

    & > .women-picture-wrapper {
      width: 100%;
      height: 100%;
      & > img {
        margin: 0 auto;
        width: 60%;

        @media (min-width: $desktop-sm-bp) {
          width: 50%;
        }
      }
    }

    & .comments__author {
      @include text($font-size: calc(30px + 0.5vw), $font-weight: 700);
      background: $primary-text-gradient;
      background-clip: text;
      -webkit-background-clip: text;
      -webkit-text-fill-color: transparent;
      text-align: center;
      margin-bottom: 30px;

      @media (min-width: $desktop-sm-bp) {
        font-size: calc(75px + 0.5vw);
      }
    }

    & .ig-stats {
      display: flex;
      margin-bottom: 25px;
      justify-content: center;
      align-items: center;
      width: 100%;
      flex-direction: column;

      @media (min-width: $desktop-sm-bp) {
        align-items: baseline;
        flex-direction: row;
        margin-bottom: 40px;
      }

      &__link {
        position: relative;
        text-align: center;
        margin-bottom: 10px;

        @media (min-width: $desktop-sm-bp) {
          border-right: 2px solid #c5c5c5e1;
          margin-right: 20px;
          padding: 0 20px 0 50px;
        }

        &::before {
          content: "";
          display: inline-block;
          position: absolute;
          background-image: url("../images/instagram-icon.svg");
          background-repeat: no-repeat;
          background-position: center;
          background-size: contain;
          width: 22px;
          height: 22px;
          top: 50%;
          transform: translateY(-50%);
          left: -20%;

          @media (min-width: $desktop-sm-bp) {
            width: 30px;
            height: 30px;
            left: 5%;
          }
        }

        & > a {
          @extend .comments__author;
          font-size: calc(16px + 0.5vw);
          margin: 0;
        }
      }
      &__followers {
        & > strong {
          @extend .comments__author;
          font-size: calc(16px + 0.5vw);
          margin-right: 5px;
        }

        & > span {
          @include text($color: #535252, $font-size: calc(12px + 0.5vw));
        }
      }
    }

    & > p {
      @include text($font-size: calc(11px + 0.5vw));
      line-height: 25px;
      @media (min-width: $desktop-sm-bp) {
        line-height: 33px;
      }
    }
  }
}

::-webkit-scrollbar {
  width: 6px;
}

::-webkit-scrollbar-thumb {
  background: linear-gradient(
    135deg,
    rgba(61, 165, 107, 1) 10%,
    rgba(61, 165, 107, 1) 25%,
    rgba(69, 125, 171, 1) 30%,
    rgba(69, 125, 171, 1) 50%,
    rgba(171, 79, 135, 1) 60%,
    rgba(171, 79, 135, 1) 70%,
    rgba(223, 67, 7, 1) 90%,
    rgba(223, 67, 7, 1) 100%
  );
}

