@import "./main-page.scss";
@import "./comments.scss";
@import "../helpers/variables";
@import "../helpers/mixins";

.about-company {
  @extend .comments;
  padding: 1.2em;
  height: unset;
  position: relative;
  &--underscore {
    padding-bottom: 25px;
    border-bottom: 5px solid #dee4e7;

    @media (min-width: $desktop-sm-bp) {
      padding-bottom: 40px;
    }
  }
  & > .top-decor {
    top: 5px;
    right: 5px;
    position: absolute;
    width: 38%;
  }
}

.about-company__inner {
  position: relative;
  padding: 3em 0;

  @media (min-width: 768px) and (min-height: 1024px) {
    padding: 5em 0;
  }

  @media (min-width: 1024px) and (min-height: 768px) {
    padding: 8em 0;
  }

  @media (min-width: 1024px) and (min-height: 1366px) {
    padding: 8em 0;
  }

  & > .bottom-decor {
    bottom: 5px;
    left: 5px;
    position: absolute;
    width: 38%;
  }
}

.about-company__list {
  position: relative;

  @media (min-width: $desktop-lg-bp) {
    padding: 5em 8em;
  }

  & > li {
    width: 100%;
    height: 100%;
    align-items: unset;
    position: relative;
    margin-bottom: 55px;

    @media (min-width: $desktop-sm-bp) {
      padding-bottom: 50px;
      margin-bottom: 60px;
    }

    & > .content-title {
      @include text($font-size: calc(25px + 0.5vw), $font-weight: 700);
      background: rgb(61, 165, 107); /* Old browsers */
      background: linear-gradient(
        135deg,
        #3da56b 6%,
        #3da56b 15%,
        #457dab 25%,
        #ab4f87 40%,
        #df4307 51%,
        #df4307 100%
      ); /* W3C, IE10+, FF16+, Chrome26+, Opera12+, Safari7+ */
      filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#3da56b', endColorstr='#df4307',GradientType=1 ); /* IE6-9 fallback on horizontal gradient */
      background-clip: text;
      -webkit-background-clip: text;
      -webkit-text-fill-color: transparent;
      text-align: left;
      margin-bottom: 25px;

      @media (min-width: $desktop-sm-bp) {
        font-size: calc(44px + 0.5vw);
        margin-bottom: 45px;
      }

      @media (min-width: 1024px) and (min-height: 1366px) {
        font-size: calc(38px + 0.5vw);
      }
    }

    & p {
      @include text($color: #314b66, $font-size: calc(11px + 0.5vw));
      line-height: 25px;
      margin-bottom: 30px;
      @media (min-width: $desktop-sm-bp) {
        line-height: 38px;
      }
    }

    & strong {
      font-weight: 700;
    }

    &:not(:last-child) {
      border-bottom: 1px solid #b7bdc09a;
    }
  }
}

.companies {
  &__list {
    margin-top: 4em;
    & > li {
      display: flex;
      flex-direction: column;

      @media (min-width: $desktop-sm-bp) {
        flex-direction: row;
      }

      &:not(:last-child) {
        margin-bottom: 3em;

        @media (min-width: $desktop-sm-bp) {
          margin-bottom: 4em;
        }
      }
    }
    // &::after {
    //   content: "";
    //   display: inline-block;
    //   position: absolute;
    //   bottom: -15%;
    //   left: -8%;
    //   width: 35%;
    //   height: 28%;
    //   background-image: url("../images/about-company/text-block-bottom-bg.png");
    //   background-repeat: no-repeat;
    //   background-position: center;
    //   background-size: contain;
    // }
  }

  &__picture {
    flex-basis: calc(16.5% + 30px);
    margin: 0 auto 3em;

    @media (min-width: $desktop-sm-bp) {
      margin: 0;
    }

    @media (min-width: 1024px) and (min-height: 1366px) {
      margin: 0 auto 3em 0;
    }
  }
  &__desc {
    flex-basis: calc(77.5% + 30px);

