@import "./helpers/variables";
@import "./helpers/mixins";
.pg-wrapper {
  height: 100% !important;
}

.main-screen {
  background-image: url("../images/main-screen/background.png");
  height: 100%;
  background-repeat: no-repeat;
  background-size: cover;
  background-position: center;
  position: relative;
}

.main-screen__burger {
  width: 30px;
  cursor: pointer;
  position: absolute;
  top: 20px;
  right: 20px;
  z-index: 11;
  transition: 0.2s linear;
  @media (min-width: $desktop-sm-bp) {
    width: 40px;
    top: 100px;
    right: 100px;
  }

  &:hover li:nth-child(2),
  &:hover li:last-child {
    width: 100%;
  }

  li {
    transition: 0.2s linear;
    width: 100%;
    height: 4px;
    background-color: #000;

    // &:first-child {
    //   width: 100%;
    // }

    // &:nth-child(2) {
    //   width: 70%;
    // }

    // &:last-child {
    //   width: 40%;
    // }

    &:not(:last-child) {
      margin-bottom: 4px;
    }
  }
}

.women-picture {
  width: 66%;
  position: absolute;
  z-index: 5;
  top: 0;
  right: 50%;
  transform: translate(50%, 1%);

  & > a {
    position: absolute;
    top: 25%;
    left: 22%;
    width: 20%;
    height: 20%;
    cursor: pointer;
    transition: 0.2s linear;
    &:hover {
      transform: scale(1.1);
    }
  }
  @media (min-width: $desktop-sm-bp) {
    width: 30%;
  }
}

.main-screen__title-wrapper {
  width: 260px;
  text-align: center;
  position: absolute;
  top: 20%;
  left: 50%;
  transform: translateX(-50%);
  @media (min-width: $desktop-sm-bp) {
    width: 900px;
    height: 145px;
  }
  @media (min-width: $desktop-lg-bp) {
    width: 1090px;
  }
}

.main-screen__title {
  @media (min-width: $desktop-sm-bp) {
    width: 100%;
    margin-bottom: 1.5em;
  }
}

.main-screen__subtitle {
  @include text($font-size: 12px);
  @media (min-width: $desktop-sm-bp) {
    font-size: 23px;
    letter-spacing: 14px;
    text-align: center;
    cursor: default;
  }
}

