@import "./helpers/variables";

.about-screen-wrapper {
  background-image: url("../images/about-products/background.png");
  background-repeat: no-repeat;
  background-position: 0 35%;
  background-size: cover;
  background-color: $primary-bg-color;
}

.about-screen__content {
  width: 100%;
  height: 100%;
  display: flex;
  align-items: center;
  justify-content: center;
}

.about-screen__desc {
  width: 870px;
  text-align: center;
  margin-top: 5em;
  visibility: visible;
  &.hidden {
    opacity: 0;
    visibility: hidden;
  }
}

.about-screen__text {
  font-family: $primary-font-stack;
  color: $primary-text-color;
  line-height: 31px;
  font-weight: 400;
  text-align: center;
  font-size: calc(13px + 0.5vw);
  margin-bottom: 52px;
}

.about-screen__title {
  @extend .about-screen__text;
  font-weight: 700;
  line-height: 70px;
  font-size: calc(50px + 0.5vw);
  text-transform: uppercase;
}

.about-screen__btn {
  @extend .about-screen__title;
  font-size: calc(20px + 0.5vw);
  line-height: unset;
  font-weight: 800;
  border: 3px solid $primary-text-color;
  border-radius: 64px;
  padding: 35px 130px;
  position: relative;
  cursor: pointer;
  &::after {
    content: "";
    display: inline-block;
    background-image: url("data:image/svg+xml,%3Csvg id='Layer_1' xmlns='http://www.w3.org/2000/svg' viewBox='0 0 26.5 43.8'%3E%3Cstyle%3E .st0%7Bfill:%23403e3e%7D %3C/style%3E%3Cpath transform='rotate(45.001 13.258 13.257)' class='st0' d='M-1.7 9.5h30V17h-30z'/%3E%3Cpath transform='rotate(-45.001 13.257 30.507)' class='st0' d='M-1.7 26.8h30v7.5h-30z'/%3E%3C/svg%3E");
    background-repeat: no-repeat;
    background-position: center;
    position: absolute;
    align-self: center;
    top: 49%;
    right: 12%;
    transform: translate(-50%, -40%);
    width: 30px;
    height: 30px;
  }
}

