@import "./helpers/variables";
@import "./helpers/mixins";
.pg-wrapper {
  height: 100% !important;
}

.main-screen {
  background-image: url("../images/main-screen/background.png");
  height: 100vh !important;
  background-repeat: no-repeat;
  background-size: cover;
  background-position: center;
  position: relative;

  @media (max-width: $desktop-sm-bp) and (min-height: 510px) {
    height: 90vh !important;
  }
}

.main-screen__burger {
  width: 30px;
  cursor: pointer;
  position: absolute;
  top: 50px;
  right: 50px;
  z-index: 22;
  transition: 0.2s linear;
  @media (min-width: $desktop-sm-bp) {
    width: 40px;
    top: 100px;
    right: 100px;
  }

  &:hover li:nth-child(2),
  &:hover li:last-child {
    width: 100%;
  }

  li {
    transition: 0.2s linear;
    width: 100%;
    height: 4px;
    background-color: #000;

    // &:first-child {
    //   width: 100%;
    // }

    // &:nth-child(2) {
    //   width: 70%;
    // }

    // &:last-child {
    //   width: 40%;
    // }

    &:not(:last-child) {
      margin-bottom: 4px;
    }
  }
}

.women-picture {
  width: 66%;
  position: absolute;
  z-index: 5;
  top: 0;
  right: 50%;
  transform: translate(50%, 1%);
  display: none;

  &.hidden {
    z-index: -1;
  }

  & > a {
    position: absolute;
    top: 25%;
    left: 22%;
    width: 20%;
    height: 20%;
    cursor: pointer;
    transition: 0.2s linear;
    &:hover {
      transform: scale(1.1);
    }
  }
  @media (min-width: $desktop-sm-bp) {
    display: block;
    width: 30%;
  }
}

.main-screen__title-wrapper {
  width: 85%;
  text-align: center;
  position: absolute;
  top: 15%;
  left: 50%;
  transform: translateX(-50%);
  @media (min-width: $desktop-sm-bp) {
    top: 20%;
    width: 900px;
    height: 145px;
  }
  @media (min-width: $desktop-lg-bp) {
    width: 1090px;
  }
}

.main-screen__title {
  margin: 0 auto;
  width: 65%;
  margin-bottom: 1em;
  @media (min-width: $desktop-sm-bp) {
    width: 100%;
    margin-bottom: 1.5em;
  }
}

.main-screen__subtitle {
  @include text($font-size: 15px);
  letter-spacing: 6px;
  @media (min-width: $desktop-sm-bp) {
    font-size: 23px;
    letter-spacing: 14px;
    text-align: center;
    cursor: default;
  }
}

.main-screen__decor-mobile {
  position: absolute;
  bottom: 0;
  left: 0;
  background-image: url("../images/main-screen/decor-bottom-mobile.png");
  background-repeat: no-repeat;
  background-size: contain;
  background-position: bottom;
  width: 100%;
  height: 50%;

  @media (min-width: $desktop-sm-bp) {
    display: none;
  }
  & > a {
    display: inline-block;
    position: absolute;
    bottom: 0;
    left: 0;
    width: 100%;
    height: 100%;
    & > span {
      position: absolute;
      left: 50%;
      transform: translateX(-50%);
      bottom: 7%;
      @include text($color: #1c70b0, $font-size: calc(10px + 0.5vw));
      text-transform: uppercase;
    }

    & > .arrow-wrapper {
      width: 100%;
      height: 100%;
      position: absolute;
      bottom: 0;
      left: 0;
      & > img {
        transform: rotate(90deg) translateY(50%);
        position: absolute;
        bottom: 15%;
        left: 50%;
        max-width: 10%;
      }
    }
  }
}

