@import "./helpers/variables";
@import "./helpers/mixins";

//Using "!important" only for overwriting Parallax.js positions styles!

.product-screen {
  position: relative;

  & > .vines {
    opacity: 1;
    width: 100%;
    position: absolute;
    top: 0;
    left: 0;
    transform: translateY(-30%);
    z-index: -1;
    visibility: visible;
    &.hidden {
      opacity: 0;
      visibility: hidden;
    }
  }
  &__parrot {
    max-width: 20%;
    width: 125px;
    height: 125px;
    position: absolute;
    top: 30% !important;
    left: 65% !important;

    @media (min-width: $desktop-sm-bp) {
      top: 20% !important;
      left: 12% !important;
    }
  }
  &__vitamins {
    z-index: 2;
    width: 100%;
    height: 100%;
    margin-top: 2em;

    @media (min-width: $desktop-sm-bp) {
      margin: 0;
      position: absolute;
      top: 0%;
      left: 0%;
      top: 15% !important;
      left: 20% !important;
    }

    & > img {
      width: 55%;
    }

    &--lg {
      max-width: 90%;
      width: 100%;
      top: -5%;
      left: -5%;

      @media (min-width: $desktop-sm-bp) {
        max-width: 80%;
        top: 20% !important;
        left: 10% !important;
      }

      & > img {
        width: 100%;
      }
    }
  }

  &__plants {
    z-index: -1;
    max-width: 200%;
    position: absolute;
    top: 20%;
    left: -10%;

    @media (min-width: $desktop-sm-bp) {
      top: 5% !important;
      left: -20% !important;
    }

    & > picture > img {
      @media (max-width: $desktop-sm-bp) {
        width: 350px;
      }
    }
  }

  &__reef {
    max-width: 100%;
    z-index: -1;
    position: absolute;
    top: -25%;
    left: -20%;

    @media (min-width: $desktop-sm-bp) {
      top: -10% !important;
      left: -10% !important;
    }

    & > img {
      @media (max-width: $desktop-sm-bp) {
        width: 100%;
      }
    }
  }

  &__fish {
    @extend .product-screen__parrot;
    left: 70% !important;
    top: 10% !important;
    @media (min-width: $desktop-sm-bp) {
      top: 0% !important;
    }
  }

  &__night {
    @extend .product-screen__reef;
    width: 100%;
    z-index: -1;
    top: -5%;
    left: -10%;

    @media (min-width: $desktop-sm-bp) {
      width: 650px;
      top: -7% !important;
      left: 16% !important;
    }

    & > img {
      @media (max-width: $desktop-sm-bp) {
        width: 75%;
      }
    }
  }

  &__owl {
    top: 10%;
    left: 60%;
    position: absolute;
    width: 65px;
    height: 65px;

    @media (min-width: $desktop-sm-bp) {
      width: 90px;
      height: 90px;
      top: 20% !important;
      left: 68% !important;
    }
  }
  &__forest {
    @extend .product-screen__reef;
    max-width: 105%;
    left: 0% !important;
    top: 2% !important;

    @media (min-wdith: $desktop-sm-bp) {
      left: -17% !important;
      top: -16% !important;
    }

    @media (min-width: $desktop-lg-bp) {
      left: -11% !important;
      top: -13% !important;
    }

    & > img {
      width: 81%;
    }
  }
  &__bug {
    @extend .product-screen__owl;
  }
  &__planet {
    border-radius: 50%;
    overflow: hidden;
    position: absolute !important;
    top: 23%;
    transform: translate(0, 140%) !important;

    @media (min-width: $desktop-sm-bp) {
      top: 2% !important;
    }

    @media (min-width: $desktop-lg-bp) {
      top: 9% !important;
    }

    & > div {
      width: 10vw;
      height: 10vw;
      background-image: url("../images/products-screens/omega/planet.svg");
      background-repeat: no-repeat;
      background-size: 200% 200%;
      animation: planetRotation 5s linear infinite alternate-reverse;
      transition: transform 200ms linear;

      @media (min-width: $desktop-lg-bp) {
        width: 7.5vw;
        height: 7.5vw;
      }
    }
  }
  &__spaceship {
    @extend .product-screen__owl;
    top: 5% !important;
    left: 60% !important;
    width: 80px;
    @media (min-width: $desktop-sm-bp) {
      width: 120px;
    }
  }

  &__space {
    @extend .product-screen__reef;
    max-width: 150%;
    top: -10%;
    left: -23%;
    @media (min-width: $desktop-sm-bp) {
      top: -5% !important;
    }

    & > img {
      @media (max-width: $desktop-sm-bp) {
        width: 90%;
      }
    }
  }

  &__ballons {
    max-width: 25%;
    width: 190px;
    position: absolute !important;
    top: -16%;
    z-index: -1;
    left: 0%;

    @media (min-width: $desktop-sm-bp) {
      top: 0% !important;
      left: 10% !important;
    }
  }

  &__city {
    @extend .product-screen__reef;
    top: -30%;
    left: -5%;

    @media (min-width: $desktop-sm-bp) {
      top: -5% !important;
      left: -1% !important;
    }
  }
}

@keyframes planetRotation {
  from {
    background-position: 85% 20%;
  }
  to {
    background-position: 10% 80%;
  }
}

.product-screen__content {
  height: 100%;
  display: flex;
  flex-direction: column;

  @media (max-width: $desktop-sm-bp) {
    overflow: hidden;
    padding: 0 0 2em;
  }
  @media (min-width: $desktop-sm-bp) {
    flex-direction: row;
  }
}

.product-screen__pictures {
  user-select: none;
  opacity: 1;
  position: relative;
  left: 1% !important;
  top: 10% !important;
  width: 100%;
  height: 100%;
  transition: 0.5s all;
  margin-bottom: 3em;
  &.hidden {
    opacity: 0;
  }

  @media (min-width: $desktop-sm-bp) {
    margin: 0;
  }
}

.product-screen__desc {
  width: 100%;
  z-index: 2;
  padding: 0 1em;
  align-self: center;
  @media (min-with: $desktop-sm-bp) {
    width: 55%;
    padding: 0 0 0 5em;
  }
  @media (min-width: $desktop-md-bp) {
    width: 100%;
  }
}

.product-screen__title {
  font-size: 31px;
  font-family: $primary-font-stack;
  font-weight: 700;
  color: $primary-text-color;
  text-transform: uppercase;
  margin-bottom: 0.4em;

  @media (max-width: 375px) {
    font-size: 25px;
  }

  @media (min-width: $desktop-sm-bp) {
    margin-bottom: 0.6em;
    font-size: 73px;
  }

  &--green {
    background: linear-gradient(
      45deg,
      rgba(62, 62, 62, 1) 18%,
      rgba(47, 119, 34, 1) 55%
    ); /* W3C, IE10+, FF16+, Chrome26+, Opera12+, Safari7+ */
    filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#3e3e3e', endColorstr='#2f7722',GradientType=1 ); /* IE6-9 fallback on horizontal gradient */
    @include text-clip();
  }

  &--purple {
    @extend .product-screen__title;
    background: linear-gradient(
      45deg,
      rgba(23, 25, 68, 1) 27%,
      rgba(186, 129, 226, 1) 58%
    ); /* W3C, IE10+, FF16+, Chrome26+, Opera12+, Safari7+ */
    filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#171944', endColorstr='#ba81e2',GradientType=1 ); /* IE6-9 fallback on horizontal gradient */
    @include text-clip();
  }
  &--blue {
    @extend .product-screen__title;
    background: linear-gradient(
      45deg,
      rgba(30, 44, 84, 1) 37%,
      rgba(28, 109, 201, 1) 68%
    ); /* W3C, IE10+, FF16+, Chrome26+, Opera12+, Safari7+ */
    filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#1e2c54', endColorstr='#1c6dc9',GradientType=1 ); /* IE6-9 fallback on horizontal gradient */
    @include text-clip();
  }
  &--pink {
    @extend .product-screen__title;
    background: linear-gradient(
      45deg,
      rgba(25, 17, 13, 1) 25%,
      rgba(238, 50, 195, 1) 48%
    ); /* W3C, IE10+, FF16+, Chrome26+, Opera12+, Safari7+ */
    filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#19110d', endColorstr='#ee32c3',GradientType=1 ); /* IE6-9 fallback on horizontal gradient */
    @include text-clip();
  }
  &--red {
    background: linear-gradient(
      45deg,
      rgba(35, 13, 1, 1) 22%,
      rgba(218, 31, 0, 1) 58%
    ); /* W3C, IE10+, FF16+, Chrome26+, Opera12+, Safari7+ */
    filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#230d01', endColorstr='#da1f00',GradientType=1 ); /* IE6-9 fallback on horizontal gradient */
    @include text-clip();
  }
}

.product-screen__text {
  width: 100%;
  font-size: calc(11px + 0.5vw);
  line-height: 22px;
  color: #403e3e;
  font-family: $primary-font-stack;
  font-weight: 400;
  margin-bottom: 1.5em;

  @media (max-width: 375px) {
    line-height: 17px;
  }

  @media (min-width: $desktop-sm-bp) {
    width: 600px;
    line-height: 36px;
    margin-bottom: 3.5em;
  }

  @media (min-width: $desktop-lg-bp) {
    font-size: 24px;
  }

  & strong {
    font-weight: 600;
  }
}

.product-screen__btn {
  font-family: $primary-font-stack;
  font-size: 14px;
  font-weight: 800;
  border: 5px solid transparent;
  border-radius: 44px;
  background-color: #ffffff;
  padding: 1em 2.2em 1em 6em;
  position: relative;
  display: inline-block;
  text-transform: uppercase;
  cursor: pointer;
  transition: 0.2s linear;

  @media (min-width: $desktop-sm-bp) {
    font-size: 18px;
    padding: 1.2em 2.2em 1.2em 6em;
  }

  @media (min-width: $desktop-lg-bp) {
    font-size: 22px;
  }

  &::before {
    content: "";
    display: inline-block;
    border: 6px solid $primary-bg-color;
    border-radius: 50%;
    position: absolute;
    background-position: center;
    background-size: cover;
    background-repeat: no-repeat;
    width: 65px;
    height: 65px;
    top: 0;
    left: 0;
    transform: translate(16%, -35%);

    @media (min-width: $desktop-sm-bp) {
      width: 78px;
      height: 78px;
    }

    @media (min-width: $desktop-lg-bp) {
      width: 90px;
      height: 90px;
    }
  }

  &::after {
    content: "";
    display: inline-block;
    background-repeat: no-repeat;
    background-position: center;
    position: absolute;
    top: 50%;
    right: 5%;
    transform: translate(-50%, -40%);
