sources.scss\\components\\about-screen.scss:
@import "./helpers/variables";

.about-screen-wrapper {
  background-image: url("../images/about-products/background.jpg");
  background-repeat: no-repeat;
  background-position: center;
  background-size: cover;
}

.about-screen__content {
  width: 100%;
  height: 100%;
  display: flex;
  align-items: center;
  justify-content: center;
}

.about-screen__desc {
  width: 100%;
  text-align: center;
  margin-top: 5em;
  padding: 0 1em;
  visibility: visible;
  z-index: 2;

  @media (max-width: 375px) {
    margin-top: 2em;
  }

  @media (min-width: $desktop-sm-bp) {
    padding: 0;
    width: 870px;
  }

  &.hidden {
    opacity: 0;
    visibility: hidden;
  }
}

.about-screen__text {
  font-family: $primary-font-stack;
  color: $primary-text-color;
  line-height: 21px;
  font-weight: 400;
  text-align: center;
  font-size: calc(11px + 0.5vw);
  margin-bottom: 52px;

  @media (max-width: 375px) {
    line-height: 19px;
    margin-bottom: 25px;
  }

  @media (min-width: $desktop-sm-bp) {
    font-size: calc(12px + 0.5vw);
    line-height: 35px;
  }
}

.about-screen__title {
  @extend .about-screen__text;
  font-weight: 700;
  line-height: 38px;
  font-size: calc(30px + 0.5vw);
  text-transform: uppercase;
  background: linear-gradient(
    45deg,
    rgba(31, 45, 55, 1) 27%,
    rgba(78, 126, 159, 1) 60%
  ); /* W3C, IE10+, FF16+, Chrome26+, Opera12+, Safari7+ */
  filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#1f2d37', endColorstr='#4e7e9f',GradientType=1 ); /* IE6-9 fallback on horizontal gradient */
  background-clip: text;
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  margin-bottom: 28px;

  @media (max-width: 375px) {
    font-size: calc(26px + 0.5vw);
  }

  @media (min-width: $desktop-sm-bp) {
    line-height: 70px;
    font-size: calc(40px + 0.5vw);
    margin-bottom: 52px;
  }

  @media (min-width: $desktop-md-bp) {
    font-size: calc(50px + 0.5vw);
  }
}

.about-screen__btn {
  @extend .about-screen__title;
  display: inline-block;
  font-size: calc(15px + 0.5vw);
  line-height: unset;
  border: 3px solid $primary-text-color;
  border-radius: 64px;
  padding: 15px 39px;
  position: relative;
  cursor: pointer;

  @media (min-width: $desktop-sm-bp) {
    font-size: calc(20px + 0.5vw);
    padding: 35px 130px;
  }

  &::after {
    content: "";
    display: inline-block;
    background-image: url("data:image/svg+xml,%3Csvg id='Layer_1' xmlns='http://www.w3.org/2000/svg' viewBox='0 0 26.5 43.8'%3E%3Cstyle%3E .st0%7Bfill:%23403e3e%7D %3C/style%3E%3Cpath transform='rotate(45.001 13.258 13.257)' class='st0' d='M-1.7 9.5h30V17h-30z'/%3E%3Cpath transform='rotate(-45.001 13.257 30.507)' class='st0' d='M-1.7 26.8h30v7.5h-30z'/%3E%3C/svg%3E");
    background-repeat: no-repeat;
    background-position: center;
    position: absolute;
    align-self: center;
    top: 48%;
    right: 6%;
    transform: translate(-50%, -40%);
    width: 14px;
    height: 14px;

    @media (min-width: $desktop-sm-bp) {
      top: 49%;
      right: 12%;
      transform: translate(-50%, -40%);
      width: 30px;
      height: 30px;
    }
  }
}

sources.scss\\components\\product-page.scss:
@import "./main-page.scss";
@import "../helpers/variables";
@import "../components/slider-control";
.product-page__main-screen {
  @extend .main-page__screen;
}

.product-page__inner {
  @extend .main-page__inner;
  background-image: url("../images/product-page/light-481.png");
  width: 100%;
  background-repeat: no-repeat;
  background-position: center;
  background-size: contain;
  @media (min-width: 375px) and (max-width: 400px),
    (min-width: 468px),
    (-webkit-min-device-pixel-ratio: 2),
    (min-resolution: 192dpi) {
    background-image: url("../images/product-page/light.png");
  }
}

.product-page__vitamins {
  max-width: 38%;
  height: 100%;
  margin: 0 auto;
  display: flex;
  align-items: flex-end;
}

.product-page__title-wrapper {
  @extend .main-screen__title-wrapper;
  top: 8%;
  width: 100%;
}

.product-page__title {
  @extend .main-screen__title;
  align-self: center;
  font-weight: 700;
  text-transform: uppercase;
  font-family: $primary-font-stack;
  font-size: calc(135px - 1vw);
  margin-bottom: 38px;
  background: rgb(61, 165, 107); /* Old browsers */

  background: linear-gradient(
    135deg,
    rgba(61, 165, 107, 1) 17%,
    rgba(61, 165, 107, 1) 36%,
    rgba(69, 125, 171, 1) 48%,
    rgba(171, 79, 135, 1) 52%,
    rgba(223, 67, 7, 1) 60%,
    rgba(223, 67, 7, 1) 100%
  ); /* W3C, IE10+, FF16+, Chrome26+, Opera12+, Safari7+ */
  filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#3da56b', endColorstr='#df4307',GradientType=1 ); /* IE6-9 fallback on horizontal gradient */
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
}

.product-page__subtitle {
  @extend .main-screen__subtitle;
  text-transform: uppercase;
  top: 0;
  letter-spacing: 2px;
}

.product-page__fruits {
  @extend .main-page__fruits;
  height: 100%;

  & > ul:first-child {
    top: -20% !important;
    left: 0;
    width: 50%;
    height: 100%;
    li {
      position: absolute !important;
      &:first-child {
        width: 45%;
        top: -35% !important;
      }
      &:last-child {
        width: 25%;
        top: 10% !important;
        left: 50% !important;
      }
    }
  }
  & > ul:last-child {
    right: -10%;
    width: 50%;
    height: 100%;
    li {
      position: absolute !important;
      top: 0;
      &:first-child {
        width: 20%;
        top: -45% !important;
      }
      &:last-child {
        width: 40%;
        top: -20% !important;
      }
    }
  }
}

.product-page__about {
  @extend .main-page__screen;
}

.product-page__slider-control {
  display: flex;
  align-items: center;
  justify-content: space-between;
  position: absolute;
  top: 50%;
  left: 0;
  transform: translateY(-50%);
  width: 100%;
  padding: 0 2em;
  z-index: 20;

  & > button {
    font-size: 60px;
    cursor: pointer;
    color: #acacac;
    transition: 0.1s linear;

    &:hover {
      color: #464646;
    }
  }
}

sources.scss\\components\\up-button.scss:
@import "../helpers/variables";
.up-button {
  display: none;
  position: fixed;
  right: 4%;
  bottom: 8%;
  width: 60px;
  height: 60px;
  z-index: 999;
  cursor: pointer;
  user-select: none;
  background-image: url("data:image/svg+xml,%3Csvg data-name='Layer 1' xmlns='http://www.w3.org/2000/svg' viewBox='0 0 256 256'%3E%3Cdefs%3E%3ClinearGradient id='a' y1='128' x2='256' y2='128' gradientUnits='userSpaceOnUse'%3E%3Cstop offset='0' stop-color='%23ca5424'/%3E%3Cstop offset='.26' stop-color='%23966395'/%3E%3Cstop offset='.51' stop-color='%23458fa0'/%3E%3Cstop offset='1' stop-color='%2345a575'/%3E%3C/linearGradient%3E%3C/defs%3E%3Cpath d='M128 0a128 128 0 1 0 128 128A128 128 0 0 0 128 0z' fill='url(%23a)'/%3E%3Cpath fill='%23fff' d='M186.32 141.73L128 83.4l-58.32 58.33 18.87 18.87L128 121.14l39.45 39.46 18.87-18.87z'/%3E%3C/svg%3E");

  @media (min-width: $desktop-sm-bp) {
    display: block;
  }

  &:hover {
    animation: bounce 0.7s;
  }
}

@keyframes bounce {
  0%,
  20%,
  60%,
  100% {
    -webkit-transform: translateY(0);
    transform: translateY(0);
  }

  40% {
    -webkit-transform: translateY(-20px);
    transform: translateY(-20px);
  }

  80% {
    -webkit-transform: translateY(-10px);
    transform: translateY(-10px);
  }
}

sources.scss\\components\\footer.scss:
@import "../helpers/variables";
.footer {
  background-repeat: no-repeat;
  background-position: center;
  background-size: cover;
  background-color: $primary-bg-color;
  padding: 1em;
  height: 60vh;

  @media (min-width: $desktop-sm-bp) {
    padding: 1.5em;
    height: 100vh;
  }

  &__inner {
    background-color: #fff;
    width: 100%;
    height: 100%;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;

    & > img {
      margin: 0 auto 55px;
      width: 90%;

      @media (min-width: $desktop-sm-bp) {
        margin: 0 auto 110px;
        width: 80%;
      }
    }
  }

  &__socials {
    display: flex;
    width: 200px;
    justify-content: space-between;
    align-items: center;

    @media (min-width: $desktop-sm-bp) {
      width: 295px;
    }

    &-item {
      width: 57px;
      height: 57px;
      cursor: pointer;
      transition: 0.2s linear;
      &:hover {
        transform: scale(1.1);
      }

      @media (min-width: $desktop-sm-bp) {
        width: 80px;
        height: 80px;
      }
    }
  }
}

sources.scss\\components\\menu.scss:
.menu {
  width: 98%;
  height: 95vh;
  background-color: #fff;
  z-index: 21;
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  background-image: url("../images/menu/decor.png");
  background-repeat: no-repeat;
  background-position: left;
  background-size: contain;
  display: flex;
  justify-content: center;
  align-items: center;
  visibility: visible;

  &.hidden {
    visibility: hidden;
  }

  & > ul {
    & > li {
      width: 80%;
      margin: 0 auto 60px auto;
      margin-bottom: 60px;
      position: relative;

      @media (min-width: $desktop-sm-bp) {
        width: 100%;
        margin: 0 0 60px 0;
      }

      &::before {
        content: "";
        background-image: url("../images/menu/1.png");
        background-repeat: no-repeat;
        background-size: contain;
        background-position: center;
        width: 50px;
        height: 50px;
        position: absolute;
        top: -35%;
        left: -5%;
        z-index: -1;
      }
      &:hover::before {
        animation: bounce 0.7s ease-in-out;
      }
      &:nth-child(2)::before {
        background-image: url("../images/menu/2.png");
      }
      &:nth-child(3)::before {
        background-image: url("../images/menu/3.png");
      }
      &:nth-child(4)::before {
        background-image: url("../images/menu/4.png");
      }
      &:last-child::before {
        background-image: url("../images/menu/5.png");
      }

      & > a {
        @include text(
          $color: #403e3e,
          $font-size: calc(26px + 0.5vw),
          $font-weight: 700
        );
        z-index: 1;
        @media (min-width: $desktop-sm-bp) {
          font-size: calc(45px + 0.5vw);
        }
      }
    }
  }
}

@keyframes bounce {
  0%,
  20%,
  60%,
  100% {
    -webkit-transform: translateY(0);
    transform: translateY(0);
  }

  40% {
    -webkit-transform: translateY(-20px);
    transform: scale(1.1) translateY(-20px);
  }

  80% {
    -webkit-transform: translateY(-10px);
    transform: scale(1.05) translateY(-10px);
  }
}
