@import "../helpers/variables.scss";
@import "../helpers/mixins.scss";

.preloader-screen {
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background-color: $primary-bg-color;
  z-index: 999;
  visibility: visible;
  &.done {
    visibility: hidden;
  }

  .lds-roller {
    @include on-circle($item-count: 5, $circle-size: 6em, $item-size: 5em);
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);

    & div {
      width: 40px;
      height: 40px;
      background-size: 100%;
      background-position: center;
      background-repeat: no-repeat;

      &:first-child {
        width: 50px;
        height: 50px;
        background-image: url("../images/main-screen/raspberrie.png");
      }
      &:nth-child(2) {
        background-image: url("../images/main-screen/blackberry.png");
      }
      &:nth-child(3) {
        background-image: url("../images/main-screen/strawberry.png");
      }
      &:last-child {
        background-image: url("../images/main-screen/lemon.png");
      }
    }
  }
}

sources.scss\\helpers\\mixins.scss:
@import "../helpers/variables";
@mixin text(
  $color: $primary-text-color,
  $font-family: $primary-font-stack,
  $font-size: "",
  $font-weight: 400
) {
  color: $color;
  font-family: $font-family;
  font-size: $font-size;
  font-weight: $font-weight;
}

/// Mixin to place items on a circle
/// @author Hugo Giraudel
/// @author Ana Tudor
/// @param {Integer} $item-count - Number of items on the circle
/// @param {Length} $circle-size - Large circle size
/// @param {Length} $item-size - Single item size
@mixin on-circle($item-count, $circle-size, $item-size) {
  position: relative;
  width: $circle-size;
  height: $circle-size;
  padding: 0;
  border-radius: 50%;
  list-style: none;

  > * {
    display: block;
    position: absolute;
    top: 50%;
    left: 50%;
    width: $item-size;
    height: $item-size;
    margin: -($item-size / 4);

    $angle: (360 / $item-count);
    $rot: 0;

    @for $i from 1 through $item-count {
      &:nth-of-type(#{$i}) {
        transform: rotate($rot * 1deg)
          translate($circle-size / 2)
          rotate($rot * -1deg);
      }

      $rot: $rot + $angle;
    }
  }
}

sources.scss\\components\\main-screen.scss:
@import "./helpers/variables";
@import "./helpers/mixins";
.pg-wrapper {
  height: 100% !important;
}

.main-screen {
  background-image: url("../images/main-screen/background.png");
  height: 100%;
  background-repeat: no-repeat;
  background-size: cover;
  background-position: center;
}

.main-screen__burger {
  width: 30px;
  cursor: pointer;
  position: absolute;
  top: 20px;
  right: 20px;
  transition: 0.2s linear;
  @media (min-width: $desktop-sm-bp) {
    width: 40px;
    top: 55px;
    right: 42px;
  }

  &:hover li:nth-child(2),
  &:hover li:last-child {
    width: 100%;
  }

  li {
    transition: 0.2s linear;
    width: 100%;
    height: 4px;
    background-color: #000;

    &:first-child {
      width: 100%;
    }

    &:nth-child(2) {
      width: 70%;
    }

    &:last-child {
      width: 40%;
    }

    &:not(:last-child) {
      margin-bottom: 4px;
    }
  }
}

.women-picture {
  width: 66%;
  position: absolute;
  z-index: 5;
  top: 0;
  right: 50%;
  transform: translate(50%, 1%);

  & > a {
    position: absolute;
    top: 25%;
    left: 22%;
    width: 20%;
    height: 20%;
    cursor: pointer;
    transition: 0.2s linear;
    &:hover {
      transform: scale(1.1);
    }
  }
  @media (min-width: $desktop-sm-bp) {
    width: 30%;
  }
}

.main-screen__title-wrapper {
  width: 260px;
  text-align: center;
  position: absolute;
  top: 20%;
  left: 50%;
  transform: translateX(-50%);
  @media (min-width: $desktop-sm-bp) {
    width: 900px;
    height: 145px;
  }
  @media (min-width: $desktop-lg-bp) {
    width: 1090px;
  }
}

.main-screen__title {
  @media (min-width: $desktop-sm-bp) {
    width: 100%;
    margin-bottom: 1.5em;
  }
}

.main-screen__subtitle {
  @include text($font-size: 12px);
  @media (min-width: $desktop-sm-bp) {
    font-size: 23px;
    letter-spacing: 14px;
    text-align: center;
    cursor: default;
  }
}

sources.scss\\components\\bottles-list.scss:
.bottles-list {
  display: flex;
  align-items: center;
  position: absolute !important;
  bottom: 50%;
  left: 50%;
  width: 200px;
  transform: translate(-50%, 100%) !important;

  @media (min-width: $desktop-sm-bp) {
    width: 1000px;
  }
  @media (min-width: $desktop-md-bp) {
    width: 1200px;
  }

  li {
    position: unset !important;
    width: 25%;
    height: 300px;
    transition: 0.2s linear;
    background-repeat: no-repeat;
    background-position: center;
    background-size: contain;
    &:first-child {
      background-image: url("../images/main-screen/green-bottle.png");
      background-size: cover;
    }
    &:nth-child(2) {
      background-image: url("../images/main-screen/blue-bottle.png");
    }
    &:nth-child(3) {
      background-image: url("../images/main-screen/purple-bottle.png");
    }
    &:last-child {
      background-image: url("../images/main-screen/orange-bottle.png");
    }
  }
}

.bottles__slide {
  top: 50%;
  transform: translateY(120%);
  @media (min-width: $desktop-sm-bp) {
    display: none;
  }
}

