@import "./helpers/variables";

.border {
  border: 20px solid #fff;
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  z-index: 1;
  width: 100%;
  height: 100%;
}

.main-page__screen {
  user-select: none;
  padding: 0.8em;
  position: relative;
  overflow: hidden;
  width: 100%;
  height: 100vh;
  background-color: $primary-bg-color;
  @media (min-width: $desktop-sm-bp) {
    padding: 1.5em;
  }

  &.main-page__about {
    background: none;
  }
}

.main-page__inner {
  z-index: 1;
  padding: 0.7em 0.5em;
  position: relative;
  height: 100%;
  @media (min-width: $desktop-sm-bp) {
    padding: 3.5em 5.5em;
  }
}

.main-page__logo {
  user-select: none;
  position: absolute;
  z-index: 1;
  width: 65px;
  @media (min-width: $desktop-sm-bp) {
    width: 165px;
  }
}

.main-page__fruits {
  display: flex;
  position: absolute;
  top: 0;
  left: 0;
  bottom: 100%;
  transform: translate(0, 50%);
  width: 100%;
  justify-content: space-between;
  @media (min-width: $desktop-sm-bp) {
    top: -5%;
  }
  @media (min-width: $desktop-lg-bp) {
    top: 9%;
  }

  & > ul:first-child {
    position: absolute;
    top: 0;
    left: 0;
    transform: translate(-25%, 15%) !important;

    @media (min-width: $desktop-sm-bp) {
      transform: translate(-15%, 15%) !important;
    }
    li {
      position: unset !important;
      &:first-child {
        width: calc(25px + 2vh);
        position: absolute !important;
        top: 0;
        margin-left: 7em;
      }
      &:nth-child(2) {
        width: calc(50px + 2vh);
        margin: 3em 3em 0;
      }
      &:nth-child(3) {
        width: calc(110px + 2vh);
      }
      &:nth-child(4) {
        width: calc(170px + 2vh);
      }
      @media (min-width: $desktop-sm-bp) {
        &:first-child {
          width: 80px;
          position: absolute !important;
          bottom: 0;
          margin-left: 7em;
        }
        &:nth-child(2) {
          width: 140px;
          margin: 3em 3em 0;
        }
        &:nth-child(3) {
          width: 250px;
        }
        &:nth-child(4) {
          width: 390px;
        }
      }
    }
  }

  & > ul:last-child {
    position: absolute;
    top: 0;
    right: 0;
    transform: translate(25%, 15%) !important;

    @media (min-width: $desktop-sm-bp) {
      transform: translate(20%, 15%) !important;
    }
    li {
      position: unset !important;

      &:first-child {
        width: calc(30px + 2vh);
        margin-left: 7em;
      }
      &:nth-child(2) {
        width: calc(60px + 2vh);
        margin-left: 7em;
      }
      &:nth-child(3) {
        width: calc(120px + 2vh);
        margin-left: 5em;
      }
      &:nth-child(4) {
        width: calc(200px + 2vh);
      }
      @media (min-width: $desktop-sm-bp) {
        &:first-child {
          width: 80px;
          margin-left: 7em;
        }
        &:nth-child(2) {
          width: 140px;
          margin-left: 9em;
        }
        &:nth-child(3) {
          width: 210px;
          margin-left: 5em;
        }
        &:nth-child(4) {
          width: 390px;
        }
      }
    }
  }
}

.main-page__footer {
  background-image: url("../images/about-products/background.jpg");
}

sources.scss\\components\\product-screen.scss:
@import "./helpers/variables";
@import "./helpers/mixins";

//Using "!important" only for overwriting Parallax.js positions styles!

.product-screen {
  position: relative;
  & > .vines {
    opacity: 1;
    width: 100%;
    position: absolute;
    top: 0;
    left: 0;
    transform: translateY(-30%);
    z-index: -1;
    visibility: visible;
    &.hidden {
      opacity: 0;
      visibility: hidden;
    }
  }
  &__parrot {
    width: 125px;
    height: 125px;
    position: absolute;
    top: 20% !important;
    left: 12% !important;
  }
  &__vitamins {
    max-width: 55%;
    width: 55%;
    height: 57%;
    background-repeat: no-repeat;
    background-size: cover;
    position: absolute;
    top: 15% !important;
    left: 20% !important;
    z-index: 2;

    &--lg {
      max-width: 80%;
      width: 100%;
      top: 20% !important;
      left: 10% !important;
    }
  }

  &__plants {
    z-index: -1;
    max-width: 200%;
    position: absolute;
    top: 0;
    left: -20% !important;
  }
  &__reef {
    max-width: 100%;
    z-index: -1;
    position: absolute;
    top: -10% !important;
    left: -10% !important;
  }
  &__fish {
    @extend .product-screen__parrot;
    top: 0% !important;
    left: 70% !important;
  }

  &__night {
    @extend .product-screen__reef;
    width: 650px;
    z-index: -1;
    top: -7% !important;
    left: 16% !important;
  }

  &__owl {
    top: 20% !important;
    left: 68% !important;
    position: absolute;
    width: 90px;
    height: 90px;
  }
  &__forest {
    @extend .product-screen__reef;
    max-width: 105%;
    left: -11% !important;
    top: -13% !important;
  }
  &__bug {
    @extend .product-screen__owl;
  }
  &__planet {
    border-radius: 50%;
    overflow: hidden;
    position: absolute !important;
    top: 9% !important;
    transform: translate(0, 140%) !important;
    & > div {
      width: 7.5vw;
      height: 7.5vw;
      background-image: url("../images/products-screens/omega/planet.svg");
      background-repeat: no-repeat;
      background-size: 200% 200%;
      animation: planetRotation 5s linear infinite alternate-reverse;
      transition: transform 200ms linear;
    }
  }
  &__spaceship {
    @extend .product-screen__owl;
    top: 5% !important;
    left: 60% !important;
    width: 120px;
  }

  &__space {
    @extend .product-screen__reef;
    max-width: 150%;
    top: -5% !important;
  }

  &__ballons {
    width: 190px;
    position: absolute !important;
    top: 0% !important;
    left: 10% !important;
  }

  &__city {
    @extend .product-screen__reef;
    top: -5% !important;
    left: -1% !important;
  }
}

@keyframes planetRotation {
  from {
    background-position: 85% 20%;
  }
  to {
    background-position: 10% 80%;
  }
}

.product-screen__content {
  height: 100%;
  display: flex;
}

.product-screen__pictures {
  user-select: none;
  opacity: 1;
  position: relative;
  left: 1% !important;
  top: 10% !important;
  width: 100%;
  height: 100%;
  transition: 0.5s all;
  &.hidden {
    opacity: 0;
  }
}

.product-screen__vitamins {
  &--calcivit {
    background-image: url("../images/products-screens/calcivit/vitamins.png");
    @media (-webkit-min-device-pixel-ratio: 2), (min-resolution: 192dpi) {
      background-image: url("../images/products-screens/calcivit/vitamins@2x.png");
    }
  }
  &--multivit {
    background-image: url("../images/products-screens/multivit/vitamins.png");
    @media (-webkit-min-device-pixel-ratio: 2), (min-resolution: 192dpi) {
      background-image: url("../images/products-screens/multivit/vitamins@2x.png");
    }
  }
  &--immunovit {
    background-image: url("../images/products-screens/immunovit/vitamins.png");
    @media (-webkit-min-device-pixel-ratio: 2), (min-resolution: 192dpi) {
      background-image: url("../images/products-screens/immunovit/vitamins@2x.png");
    }
  }
  &--smartvit {
    background-image: url("../images/products-screens/smartvit/vitamins.png");
    @media (-webkit-min-device-pixel-ratio: 2), (min-resolution: 192dpi) {
      background-image: url("../images/products-screens/smartvit/vitamins@2x.png");
    }
  }
  &--omega {
    background-image: url("../images/products-screens/omega/vitamins.png");
    @media (-webkit-min-device-pixel-ratio: 2), (min-resolution: 192dpi) {
      background-image: url("../images/products-screens/omega/vitamins@2x.png");
    }
  }
}

.product-screen__desc {
  width: 100%;
  z-index: 2;
  padding: 0 0 0 5em;
  align-self: center;
}

.product-screen__title {
  font-size: 73px;
  color: #403e3e;
  font-family: $primary-font-stack;
  font-weight: 700;
  text-transform: uppercase;
  margin-bottom: 0.6em;
}

.product-screen__text {
  width: 600px;
  font-size: 24px;
  line-height: 36px;
  color: #403e3e;
  font-family: $primary-font-stack;
  font-weight: 400;
  margin-bottom: 3.5em;

  & strong {
    font-weight: 600;
  }
}

.product-screen__btn {
  font-family: $primary-font-stack;
  font-size: 22px;
  font-weight: 800;
  border: 5px solid transparent;
  border-radius: 44px;
  background-color: #ffffff;
  padding: 1.2em 2.2em 1.2em 6em;
  position: relative;
  display: inline-block;
  text-transform: uppercase;
  cursor: pointer;
  transition: 0.2s linear;

  &::before {
    content: "";
    display: inline-block;
    border: 6px solid $primary-bg-color;
    border-radius: 50%;
    position: absolute;
    background-position: center;
    background-size: cover;
    background-repeat: no-repeat;
    width: 90px;
    height: 90px;
    top: 0;
    left: 0;
    transform: translate(16%, -35%);
  }

  &::after {
    content: "";
    display: inline-block;
    background-repeat: no-repeat;
    background-position: center;
    position: absolute;
    top: 50%;
    right: 5%;
    transform: translate(-50%, -40%);
    width: 18px;
    height: 18px;
  }
  &--green {
    color: $green-btn;
    &:hover {
      border-color: $green-btn;
    }
    &::after {
      background-image: url("data:image/svg+xml,%3Csvg id='Layer_1' xmlns='http://www.w3.org/2000/svg' viewBox='0 0 26.5 43.8'%3E%3Cstyle%3E .st0%7Bfill:%2300a042%7D %3C/style%3E%3Cpath transform='rotate(45.001 13.258 13.257)' class='st0' d='M-1.7 9.5h30V17h-30z'/%3E%3Cpath transform='rotate(-45.001 13.257 30.507)' class='st0' d='M-1.7 26.8h30v7.5h-30z'/%3E%3C/svg%3E");
    }
    &::before {
      background-image: url("../images/products-screens/calcivit/btn-icon.png");
    }
  }
  &--blue {
    &:hover {
      border-color: $blue-btn;
    }
    color: $blue-btn;
    &::after {
      background-image: url("data:image/svg+xml,%3Csvg id='Layer_1' xmlns='http://www.w3.org/2000/svg' viewBox='0 0 26.5 43.8'%3E%3Cstyle%3E .st0%7Bfill:%23006d9e%7D %3C/style%3E%3Cpath transform='rotate(45.001 13.258 13.257)' class='st0' d='M-1.7 9.5h30V17h-30z'/%3E%3Cpath transform='rotate(-45.001 13.257 30.507)' class='st0' d='M-1.7 26.8h30v7.5h-30z'/%3E%3C/svg%3E");
    }
    &::before {
      background-image: url("../images/products-screens/multivit/btn-icon.png");
    }
  }
  &--purple {
    color: $purple-btn;
    &:hover {
      border-color: $purple-btn;
    }
    &::after {
      background-image: url("data:image/svg+xml,%3Csvg id='Layer_1' xmlns='http://www.w3.org/2000/svg' viewBox='0 0 26.5 43.8'%3E%3Cstyle%3E .st0%7Bfill:%239e1679%7D %3C/style%3E%3Cpath transform='rotate(45.001 13.258 13.257)' class='st0' d='M-1.7 9.5h30V17h-30z'/%3E%3Cpath transform='rotate(-45.001 13.257 30.507)' class='st0' d='M-1.7 26.8h30v7.5h-30z'/%3E%3C/svg%3E");
    }
    &::before {
      background-image: url("../images/products-screens/immunovit/btn-icon.png");
    }
  }
  &--orange {
    color: $orange-btn;
    &:hover {
      border-color: $orange-btn;
    }
    &::after {
      background-image: url("data:image/svg+xml,%3Csvg id='Layer_1' xmlns='http://www.w3.org/2000/svg' viewBox='0 0 26.5 43.8'%3E%3Cstyle%3E .st0%7Bfill:%23de3c16%7D %3C/style%3E%3Cpath transform='rotate(45.001 13.258 13.257)' class='st0' d='M-1.7 9.5h30V17h-30z'/%3E%3Cpath transform='rotate(-45.001 13.257 30.507)' class='st0' d='M-1.7 26.8h30v7.5h-30z'/%3E%3C/svg%3E");
    }
    &::before {
      background-image: url("../images/products-screens/smartvit/btn-icon.png");
    }
  }
  &--pink {
    color: $pink-btn;
    &:hover {
      border-color: $pink-btn;
    }
    &::after {
      background-image: url("data:image/svg+xml,%3Csvg id='Layer_1' xmlns='http://www.w3.org/2000/svg' viewBox='0 0 26.5 43.8'%3E%3Cstyle%3E .st0%7Bfill:%23ec2fc1%7D %3C/style%3E%3Cpath transform='rotate(45.001 13.258 13.257)' class='st0' d='M-1.7 9.5h30V17h-30z'/%3E%3Cpath transform='rotate(-45.001 13.257 30.507)' class='st0' d='M-1.7 26.8h30v7.5h-30z'/%3E%3C/svg%3E");
    }
    &::before {
      background-image: url("../images/products-screens/hello-kitty/btn-icon.png");
    }
  }
}

sources.scss\\components\\about-screen.scss:
@import "./helpers/variables";

.about-screen-wrapper {
  background-image: url("../images/about-products/background.jpg");
  background-repeat: no-repeat;
  background-position: center;
  background-size: cover;
}

.about-screen__content {
  width: 100%;
  height: 100%;
  display: flex;
  align-items: center;
  justify-content: center;
}

.about-screen__desc {
  width: 870px;
  text-align: center;
  margin-top: 5em;
  visibility: visible;
  z-index: 2;
  &.hidden {
    opacity: 0;
    visibility: hidden;
  }
}

.about-screen__text {
  font-family: $primary-font-stack;
  color: $primary-text-color;
  line-height: 35px;
  font-weight: 400;
  text-align: center;
  font-size: calc(12px + 0.5vw);
  margin-bottom: 52px;
}

.about-screen__title {
  @extend .about-screen__text;
  font-weight: 700;
  line-height: 70px;
  font-size: calc(50px + 0.5vw);
  text-transform: uppercase;
}

.about-screen__btn {
  @extend .about-screen__title;
  font-size: calc(20px + 0.5vw);
  line-height: unset;
  font-weight: 800;
  border: 3px solid $primary-text-color;
  border-radius: 64px;
  padding: 35px 130px;
  position: relative;
  cursor: pointer;
  &::after {
    content: "";
    display: inline-block;
    background-image: url("data:image/svg+xml,%3Csvg id='Layer_1' xmlns='http://www.w3.org/2000/svg' viewBox='0 0 26.5 43.8'%3E%3Cstyle%3E .st0%7Bfill:%23403e3e%7D %3C/style%3E%3Cpath transform='rotate(45.001 13.258 13.257)' class='st0' d='M-1.7 9.5h30V17h-30z'/%3E%3Cpath transform='rotate(-45.001 13.257 30.507)' class='st0' d='M-1.7 26.8h30v7.5h-30z'/%3E%3C/svg%3E");
    background-repeat: no-repeat;
    background-position: center;
    position: absolute;
    align-self: center;
    top: 49%;
    right: 12%;
    transform: translate(-50%, -40%);
    width: 30px;
    height: 30px;
  }
}

sources.scss\\components\\product-page.scss:
@import "./main-page.scss";
@import "../helpers/variables";
.product-page__main-screen {
  @extend .main-page__screen;
}

.product-page__inner {
  @extend .main-page__inner;
  background-image: url("../images/product-page/light-481.png");
  width: 100%;
  background-repeat: no-repeat;
  background-position: center;
  background-size: contain;
  @media (min-width: 375px) and (max-width: 400px),
    (min-width: 468px),
    (-webkit-min-device-pixel-ratio: 2),
    (min-resolution: 192dpi) {
    background-image: url("../images/product-page/light.png");
  }
}

.product-page__vitamins {
  max-width: 38%;
  height: 100%;
  margin: 0 auto;
  display: flex;
  align-items: flex-end;
}

.product-page__title-wrapper {
  @extend .main-screen__title-wrapper;
  top: 8%;
  width: 100%;
}

.product-page__title {
  @extend .main-screen__title;
  align-self: center;
  font-weight: 700;
  text-transform: uppercase;
  font-family: $primary-font-stack;
  font-size: calc(135px - 1vw);
  margin-bottom: 38px;
  background: rgb(61, 165, 107); /* Old browsers */
  background: -moz-linear-gradient(
    -45deg,
    rgba(61, 165, 107, 1) 17%,
    rgba(61, 165, 107, 1) 36%,
    rgba(69, 125, 171, 1) 48%,
    rgba(171, 79, 135, 1) 52%,
    rgba(223, 67, 7, 1) 60%,
    rgba(223, 67, 7, 1) 100%
  ); /* FF3.6-15 */
  background: -webkit-linear-gradient(
    -45deg,
    rgba(61, 165, 107, 1) 17%,
    rgba(61, 165, 107, 1) 36%,
    rgba(69, 125, 171, 1) 48%,
    rgba(171, 79, 135, 1) 52%,
    rgba(223, 67, 7, 1) 60%,
    rgba(223, 67, 7, 1) 100%
  ); /* Chrome10-25,Safari5.1-6 */
  background: linear-gradient(
    135deg,
    rgba(61, 165, 107, 1) 17%,
    rgba(61, 165, 107, 1) 36%,
    rgba(69, 125, 171, 1) 48%,
    rgba(171, 79, 135, 1) 52%,
    rgba(223, 67, 7, 1) 60%,
    rgba(223, 67, 7, 1) 100%
  ); /* W3C, IE10+, FF16+, Chrome26+, Opera12+, Safari7+ */
  filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#3da56b', endColorstr='#df4307',GradientType=1 ); /* IE6-9 fallback on horizontal gradient */
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
}

.product-page__subtitle {
  @extend .main-screen__subtitle;
  text-transform: uppercase;
  top: 0;
  letter-spacing: 2px;
}

.product-page__fruits {
  @extend .main-page__fruits;
  height: 100%;

  & > ul:first-child {
    top: -20% !important;
    left: 0;
    width: 50%;
    height: 100%;
    li {
      position: absolute !important;
      &:first-child {
        width: 45%;
        top: -35% !important;
      }
      &:last-child {
        width: 25%;
        top: 10% !important;
        left: 50% !important;
      }
    }
  }
  & > ul:last-child {
    right: -10%;
    width: 50%;
    height: 100%;
    li {
      position: absolute !important;
      top: 0;
      &:first-child {
        width: 20%;
        top: -45% !important;
      }
      &:last-child {
        width: 40%;
        top: -20% !important;
      }
    }
  }
}

.product-page__about {
  @extend .main-page__screen;
}

sources.scss\\components\\up-button.scss:
@import "../helpers/variables";
.up-button {
  position: fixed;
  right: 8%;
  bottom: 10%;
  width: 60px;
  height: 60px;
  z-index: 999;
  cursor: pointer;
  user-select: none;
  background-image: url("data:image/svg+xml,%3Csvg data-name='Layer 1' xmlns='http://www.w3.org/2000/svg' viewBox='0 0 256 256'%3E%3Cdefs%3E%3ClinearGradient id='a' y1='128' x2='256' y2='128' gradientUnits='userSpaceOnUse'%3E%3Cstop offset='0' stop-color='%23ca5424'/%3E%3Cstop offset='.26' stop-color='%23966395'/%3E%3Cstop offset='.51' stop-color='%23458fa0'/%3E%3Cstop offset='1' stop-color='%2345a575'/%3E%3C/linearGradient%3E%3C/defs%3E%3Cpath d='M128 0a128 128 0 1 0 128 128A128 128 0 0 0 128 0z' fill='url(%23a)'/%3E%3Cpath fill='%23fff' d='M186.32 141.73L128 83.4l-58.32 58.33 18.87 18.87L128 121.14l39.45 39.46 18.87-18.87z'/%3E%3C/svg%3E");

  &:hover {
    animation: bounce 0.7s;
  }
}

@keyframes bounce {
  0%,
  20%,
  60%,
  100% {
    -webkit-transform: translateY(0);
    transform: translateY(0);
  }

  40% {
    -webkit-transform: translateY(-20px);
    transform: translateY(-20px);
  }

  80% {
    -webkit-transform: translateY(-10px);
    transform: translateY(-10px);
  }
}

sources.scss\\components\\footer.scss:
@import "../helpers/variables";
.footer {
  background-repeat: no-repeat;
  background-position: center;
  background-size: cover;
  background-color: $primary-bg-color;
  padding: 1.5em;
  height: 100vh;

  &__inner {
    background-color: #fff;
    width: 100%;
    height: 100%;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;

    & > img {
      margin: 0 auto 110px;
    }
  }

  &__socials {
    display: flex;
    width: 295px;
    justify-content: space-between;
    align-items: center;
    &-item {
      width: 80px;
      height: 80px;
      cursor: pointer;
      transition: 0.2s linear;
      &:hover {
        transform: scale(1.1);
      }
    }
  }
}
