sources.scss\\components\\main-page.scss:
@import "./slider-control";
@import "./helpers/variables";
@import "./helpers/mixins";

.border {
  border: 13px solid #fff;
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  z-index: 1;
  width: 100%;
  height: 100%;
  @media (min-width: $laptop-sm-bp) {
    padding: 1.5em;
    border: 20px solid #fff;
  }
}

.main-page__screen {
  user-select: none;
  padding: 0.8em;
  position: relative;
  overflow: hidden;
  width: 100%;
  height: unset;
  background-color: $primary-bg-color;
  @media (min-width: $laptop-sm-bp) {
    padding: 1.5em;
    height: 100%;
  }

  @media (min-width: $desktop-sm-bp) {
    height: 100vh;
  }

  &.main-page__about {
    background: none;
  }
}

.main-page__inner {
  z-index: 1;
  padding: 0.7em 0.5em;
  position: relative;
  height: 100%;

  @media (min-width: $desktop-sm-bp) {
    // padding: 3.5em 5.5em;
  }
  & > nav {
    width: 100%;
    height: 100vh;
    background-color: #fff;
    z-index: 21;
    position: absolute;
    top: 0;
    left: 0;
    display: flex;
    justify-content: center;
    align-items: center;
    visibility: visible;

    @media (min-width: $laptop-sm-bp) {
      background-image: url("../images/menu/decor.png");
      background-repeat: no-repeat;
      background-position: left;
      background-size: contain;
    }

    &.hidden {
      visibility: hidden;
    }

    & > ul {
      & > li {
        width: 80%;
        margin: 0 auto 60px auto;
        margin-bottom: 60px;
        position: relative;

        @media (min-width: $landscape-bp) {
          margin-bottom: 40px;
        }

        @media (min-width: $tablet-bp) {
          width: 100%;
          margin: 0 0 60px 0;
        }

        &:last-child {
          @media (min-width: $landscape-bp) {
            margin-bottom: 0;
          }
        }

        &::before {
          content: "";
          background-image: url("../images/menu/1.png");
          background-repeat: no-repeat;
          background-size: contain;
          background-position: center;
          width: 50px;
          height: 50px;
          position: absolute;
          top: -35%;
          left: -5%;
          z-index: -1;

          @media (min-width: $landscape-bp) {
            width: 35px;
            height: 35px;
          }

          @media (min-width: $tablet-bp) {
            width: 50px;
            height: 50px;
          }
        }
        &:hover::before {
          animation: bounce 0.7s ease-in-out;
        }
        &:nth-child(2)::before {
          background-image: url("../images/menu/2.png");
        }
        &:nth-child(3)::before {
          background-image: url("../images/menu/3.png");
        }
        &:nth-child(4)::before {
          background-image: url("../images/menu/4.png");
        }
        &:last-child::before {
          background-image: url("../images/menu/5.png");
        }

        & > a {
          @include text(
            $color: #403e3e,
            $font-size: calc(26px + 0.5vw),
            $font-weight: 700
          );
          z-index: 1;

          @media (min-width: $landscape-bp) {
            font-size: calc(19px + 0.5vw);
          }

          @media (min-width: $tablet-bp) {
            font-size: calc(45px + 0.5vw);
          }
        }
      }
    }
  }
}

.main-page__logo {
  user-select: none;
  position: absolute;
  top: 25px;
  left: 25px;
  z-index: 2;
  width: 70px;
  @media (min-width: $laptop-sm-bp) {
    width: 165px;
    top: 45px;
    left: 80px;
  }

  &.mobile-logo {
    @media (max-width: $laptop-sm-bp) {
      display: none;
    }
  }
}

.main-page__fruits {
  display: none;
  position: absolute;
  top: 0;
  left: 0;
  bottom: 100%;
  transform: translate(0, 50%);
  width: 100%;
  justify-content: space-between;
  @media (min-width: $tablet-bp) {
    display: flex;
    top: -5%;
  }
  @media (min-width: $desktop-lg-bp) {
    top: 9%;
  }

  & > ul:first-child {
    position: absolute;
    top: 0;
    left: 0;
    transform: translate(-25%, 15%) !important;

    @media (min-width: $laptop-sm-bp) {
      transform: translate(-15%, 15%) !important;
    }
    li {
      position: unset !important;
      &:first-child {
        position: absolute !important;
        top: 0;
        margin-left: 7em;
        max-width: 14%;
      }
      &:nth-child(2) {
        margin: 3em 3em 0;
        max-width: 27%;
      }
      &:nth-child(3) {
        max-width: 55%;
      }
      &:nth-child(4) {
        max-width: 85%;
      }
      @media (min-width: $tablet-bp) {
        &:first-child {
          position: absolute !important;
          bottom: 0;
          margin-left: 7em;
        }
        &:nth-child(2) {
          margin: 3em 3em 0;
        }
      }
    }
  }

  & > ul:last-child {
    position: absolute;
    top: 0;
    right: -100px;
    transform: translate(25%, 15%) !important;

    @media (min-width: $laptop-sm-bp) {
      transform: translate(20%, 15%) !important;
    }
    li {
      position: unset !important;

      &:first-child {
        max-width: 12%;
        margin-left: 12em;
      }
      &:nth-child(2) {
        max-width: 25%;
        margin-left: 7em;
      }
      &:nth-child(3) {
        max-width: 40%;
        margin-left: 5em;
      }
      &:nth-child(4) {
        max-width: 85%;
      }
      @media (min-width: $tablet-bp) {
        &:first-child {
          margin-left: 12em;
        }
        &:nth-child(2) {
          margin-left: 9em;
        }
        &:nth-child(3) {
          margin-left: 5em;
        }
      }
    }
  }
}

.main-page__footer {
  background-image: url("../images/about-products/background.jpg");
}

.main-page__slider-control {
  @extend .slider-control;
  top: 55%;
}

@keyframes bounce {
  0%,
  20%,
  60%,
  100% {
    -webkit-transform: translateY(0);
    transform: translateY(0);
  }

  40% {
    -webkit-transform: translateY(-20px);
    transform: scale(1.1) translateY(-20px);
  }

  80% {
    -webkit-transform: translateY(-10px);
    transform: scale(1.05) translateY(-10px);
  }
}

sources.scss\\components\\product-screen.scss:
@import "./helpers/variables";
@import "./helpers/mixins";

//Using "!important" only for overwriting Parallax.js positions styles!

.product-screen {
  position: relative;

  & > .vines {
    opacity: 1;
    width: 100%;
    position: absolute;
    top: 0;
    left: 0;
    transform: translateY(-30%);
    z-index: -1;
    visibility: visible;
    &.hidden {
      opacity: 0;
      visibility: hidden;
    }
  }
  &__parrot {
    max-width: 20%;
    width: 125px;
    height: 125px;
    position: absolute;
    top: 30% !important;
    left: 65% !important;

    @media (min-width: $desktop-sm-bp) {
      top: 20% !important;
      left: 12% !important;
    }
  }
  &__vitamins {
    z-index: 2;
    width: 100%;
    height: 100%;
    margin-top: 3em;

    @media (min-width: $tablet-bp) {
      position: absolute;
      margin: 0;
      top: 60px !important;
      left: 34% !important;
    }

    @media (min-width: $desktop-sm-bp) {
      top: 19% !important;
      left: 34% !important;
    }

    & > img {
      max-width: 59%;
    }

    &--lg {
      width: 95%;

      @media (min-width: $tablet-bp) {
        top: 110px !important;
        left: 8% !important;
      }

      @media (min-width: $desktop-sm-bp) {
        max-width: 100%;
        top: 28% !important;
        left: 10% !important;
      }

      & > img {
        max-width: 100%;
        @media (min-width: $tablet-bp) {
          max-width: 90%;
        }
      }
    }
  }

  &__plants {
    z-index: -1;
    max-width: 200%;
    position: absolute;
    top: 20%;
    left: -10%;

    @media (min-width: $desktop-sm-bp) {
      top: 10% !important;
      left: -10% !important;
    }

    & > picture > img {
      @media (max-width: 767px) {
        width: 350px;
      }
    }
  }

  &__reef {
    max-width: 100%;
    z-index: -1;
    position: absolute;
    top: -10%;
    left: -35%;

    @media (min-width: $desktop-sm-bp) {
      top: 0% !important;
      left: -10% !important;
    }

    & > img {
      @media (max-width: $desktop-sm-bp) {
        width: 100%;
      }
    }
  }

  &__fish {
    @extend .product-screen__parrot;
    left: 70% !important;
    top: 10% !important;
    @media (min-width: $desktop-sm-bp) {
      top: 2% !important;
    }
  }

  &__night {
    @extend .product-screen__reef;
    width: 100%;
    z-index: -1;
    top: 0%;
    left: -10%;

    @media (min-width: $tablet-bp) {
      left: 20%;
    }

    @media (min-width: $desktop-sm-bp) {
      max-width: 81%;
      top: 0% !important;
      left: 16% !important;
    }

    & > img {
      @media (max-width: $desktop-sm-bp) {
        width: 75%;
      }
    }
  }

  &__owl {
    top: 10%;
    left: 60%;
    position: absolute;
    width: 65px;
    height: 65px;

    @media (min-width: $tablet-bp) {
      top: 10% !important;
    }

    @media (min-width: $desktop-sm-bp) {
      width: 90px;
      height: 90px;
      top: 20% !important;
      left: 80% !important;
    }
  }
  &__forest {
    @extend .product-screen__reef;
    max-width: 93%;
    left: -1% !important;
    top: -1% !important;

    @media (min-wdith: $tablet-bp) {
      max-width: 93%;
    }

    @media (min-wdith: $desktop-sm-bp) {
      left: -17% !important;
      top: -16% !important;
    }

    @media (min-width: $desktop-lg-bp) {
      // left: -11% !important;
      // top: -13% !important;
    }

    & > img {
      width: 100%;
    }
  }
  &__bug {
    @extend .product-screen__owl;
  }
  &__planet {
    border-radius: 50%;
    overflow: hidden;
    position: absolute !important;
    top: 23%;
    transform: translate(0, 140%) !important;
    display: none;

    @media (min-width: $tablet-bp) {
      top: 25%;
      left: -9%;
      display: block;
    }

    @media (min-width: $desktop-sm-bp) {
      top: 10% !important;
      left: 0 !important;
    }

    @media (min-width: $desktop-lg-bp) {
      top: 17% !important;
    }

    & > div {
      width: 10vw;
      height: 10vw;
      background-image: url("../images/products-screens/omega/planet.svg");
      background-repeat: no-repeat;
      background-size: 200% 200%;
      animation: planetRotation 5s linear infinite alternate-reverse;
      transition: transform 200ms linear;

      @media (min-width: $tablet-bp) {
        width: 9vw;
        height: 9vw;
      }

      @media (min-width: $desktop-lg-bp) {
        width: 7.5vw;
        height: 7.5vw;
      }
    }
  }
  &__spaceship {
    @extend .product-screen__owl;
    top: 5% !important;
    left: 60% !important;
    width: 80px;
    @media (min-width: $desktop-sm-bp) {
      width: 120px;
    }
  }

  &__space {
    @extend .product-screen__reef;
    max-width: 100%;
    top: 3%;
    left: -30%;

    @media (min-width: $tablet-bp) {
      top: 20%;
      left: 0%;
    }

    @media (min-width: $desktop-sm-bp) {
      top: 9% !important;
      left: 0% !important;
    }

    & > img {
      @media (max-width: $desktop-sm-bp) {
        width: 100%;
      }
    }
  }

  &__ballons {
    max-width: 25%;
    width: 190px;
    position: absolute !important;
    top: 0;
    z-index: -1;
    left: 0%;

    @media (min-width: $desktop-sm-bp) {
      top: 5% !important;
      left: 10% !important;
    }
  }

  &__city {
    @extend .product-screen__reef;
    top: 0;
    left: 5%;
    width: 80%;
    @media (min-width: $desktop-sm-bp) {
      top: 10% !important;
      left: 5% !important;
    }
  }
}

@keyframes planetRotation {
  from {
    background-position: 85% 20%;
  }
  to {
    background-position: 10% 80%;
  }
}

.product-screen__content {
  height: 100%;
  display: flex;
  flex-direction: column;

  @media (max-width: 1023px) {
    overflow: hidden;
    padding: 0 0 2em;
  }
  @media (min-width: $landscape-bp) {
    flex-direction: row;
  }
}

.product-screen__pictures {
  user-select: none;
  opacity: 1;
  position: relative;
  left: 1%;
  top: 10%;
  width: 100%;
  height: 100%;
  transition: 0.5s all;
  margin-bottom: 3em;
  &.hidden {
    opacity: 0;
  }

  @media (min-width: $desktop-sm-bp) {
    margin: 0;
    top: 0 !important;
    left: 0 !important;
  }
}

.product-screen__desc {
  width: 100%;
  z-index: 2;
  padding: 0 1em;
  align-self: center;

  @media (min-width: $landscape-bp) {
    padding: 3em 1em;
  }

  @media (min-width: $tablet-bp) {
    padding: 5em 1em;
  }

  @media (min-width: $laptop-sm-bp) {
    padding: 7em 1em;
  }

  @media (min-with: $desktop-sm-bp) {
    width: 55%;
    padding: 0 0 0 5em;
  }
  @media (min-width: $desktop-md-bp) {
    width: 100%;
  }
}

.product-screen__title {
  font-size: 31px;
  font-family: $primary-font-stack;
  font-weight: 700;
  color: $primary-text-color;
  text-transform: uppercase;
  margin-bottom: 0.4em;

  @media (max-width: 375px) {
    font-size: 25px;
  }

  @media (min-width: $desktop-sm-bp) {
    margin-bottom: 0.6em;
    font-size: 73px;
  }

  &--green {
    background: linear-gradient(
      45deg,
      rgba(62, 62, 62, 1) 18%,
      rgba(47, 119, 34, 1) 55%
    ); /* W3C, IE10+, FF16+, Chrome26+, Opera12+, Safari7+ */
    filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#3e3e3e', endColorstr='#2f7722',GradientType=1 ); /* IE6-9 fallback on horizontal gradient */
    @include text-clip();
  }

  &--purple {
    @extend .product-screen__title;
    background: linear-gradient(
      45deg,
      rgba(23, 25, 68, 1) 27%,
      rgba(186, 129, 226, 1) 58%
    ); /* W3C, IE10+, FF16+, Chrome26+, Opera12+, Safari7+ */
    filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#171944', endColorstr='#ba81e2',GradientType=1 ); /* IE6-9 fallback on horizontal gradient */
    @include text-clip();
  }
  &--blue {
    @extend .product-screen__title;
    background: linear-gradient(
      45deg,
      rgba(30, 44, 84, 1) 37%,
      rgba(28, 109, 201, 1) 68%
    ); /* W3C, IE10+, FF16+, Chrome26+, Opera12+, Safari7+ */
    filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#1e2c54', endColorstr='#1c6dc9',GradientType=1 ); /* IE6-9 fallback on horizontal gradient */
    @include text-clip();
  }
  &--pink {
    @extend .product-screen__title;
    background: linear-gradient(
      45deg,
      rgba(25, 17, 13, 1) 25%,
      rgba(238, 50, 195, 1) 48%
    ); /* W3C, IE10+, FF16+, Chrome26+, Opera12+, Safari7+ */
    filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#19110d', endColorstr='#ee32c3',GradientType=1 ); /* IE6-9 fallback on horizontal gradient */
    @include text-clip();
  }
  &--red {
    background: linear-gradient(
      45deg,
      rgba(35, 13, 1, 1) 22%,
      rgba(218, 31, 0, 1) 58%
    ); /* W3C, IE10+, FF16+, Chrome26+, Opera12+, Safari7+ */
    filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#230d01', endColorstr='#da1f00',GradientType=1 ); /* IE6-9 fallback on horizontal gradient */
    @include text-clip();
  }
}

.product-screen__text {
  width: 100%;
  font-size: calc(11px + 0.5vw);
  line-height: 22px;
  color: #403e3e;
  font-family: $primary-font-stack;
  font-weight: 400;
  margin-bottom: 1.5em;

  @media (max-width: 375px) {
    line-height: 17px;
  }

  @media (min-width: $desktop-sm-bp) {
    width: 600px;
    line-height: 36px;
    margin-bottom: 3.5em;
  }

  @media (min-width: $desktop-lg-bp) {
    font-size: 24px;
  }

  & strong {
    font-weight: 600;
  }
}

.product-screen__btn {
  font-family: $primary-font-stack;
  font-size: 14px;
  font-weight: 800;
  border: 5px solid transparent;
  border-radius: 44px;
  background-color: #ffffff;
  padding: 1em 2.2em 1em 6em;
  position: relative;
  display: inline-block;
  text-transform: uppercase;
  cursor: pointer;
  transition: 0.2s linear;

  @media (min-width: $desktop-sm-bp) {
    font-size: 18px;
    padding: 1.2em 2.2em 1.2em 6em;
  }

  @media (min-width: $desktop-lg-bp) {
    font-size: 22px;
  }

  &::before {
    content: "";
    display: inline-block;
    border: 6px solid $primary-bg-color;
    border-radius: 50%;
    position: absolute;
    background-position: center;
    background-size: cover;
    background-repeat: no-repeat;
    width: 65px;
    height: 65px;
    top: 0;
    left: 0;
    transform: translate(16%, -35%);

    @media (min-width: $desktop-sm-bp) {
      width: 78px;
      height: 78px;
    }

    @media (min-width: $desktop-lg-bp) {
      width: 90px;
      height: 90px;
    }
  }

  &::after {
    content: "";
    display: inline-block;
    background-repeat: no-repeat;
    background-position: center;
    position: absolute;
    top: 50%;
    right: 5%;
    transform: translate(-50%, -40%);
    width: 10px;
    height: 10px;

    @media (min-width: $desktop-sm-bp) {
      width: 18px;
      height: 18px;
    }
  }
  &--green {
    color: $green-btn;
    &:hover {
      border-color: $green-btn;
    }
    &::after {
      background-image: url("data:image/svg+xml,%3Csvg id='Layer_1' xmlns='http://www.w3.org/2000/svg' viewBox='0 0 26.5 43.8'%3E%3Cstyle%3E .st0%7Bfill:%2300a042%7D %3C/style%3E%3Cpath transform='rotate(45.001 13.258 13.257)' class='st0' d='M-1.7 9.5h30V17h-30z'/%3E%3Cpath transform='rotate(-45.001 13.257 30.507)' class='st0' d='M-1.7 26.8h30v7.5h-30z'/%3E%3C/svg%3E");
    }
    &::before {
      background-image: url("../images/products-screens/calcivit/btn-icon.png");
    }
  }
  &--blue {
    &:hover {
      border-color: $blue-btn;
    }
    color: $blue-btn;
    &::after {
      background-image: url("data:image/svg+xml,%3Csvg id='Layer_1' xmlns='http://www.w3.org/2000/svg' viewBox='0 0 26.5 43.8'%3E%3Cstyle%3E .st0%7Bfill:%23006d9e%7D %3C/style%3E%3Cpath transform='rotate(45.001 13.258 13.257)' class='st0' d='M-1.7 9.5h30V17h-30z'/%3E%3Cpath transform='rotate(-45.001 13.257 30.507)' class='st0' d='M-1.7 26.8h30v7.5h-30z'/%3E%3C/svg%3E");
    }
    &::before {
      background-image: url("../images/products-screens/multivit/btn-icon.png");
    }
  }
  &--purple {
    color: $purple-btn;
    &:hover {
      border-color: $purple-btn;
    }
    &::after {
      background-image: url("data:image/svg+xml,%3Csvg id='Layer_1' xmlns='http://www.w3.org/2000/svg' viewBox='0 0 26.5 43.8'%3E%3Cstyle%3E .st0%7Bfill:%239e1679%7D %3C/style%3E%3Cpath transform='rotate(45.001 13.258 13.257)' class='st0' d='M-1.7 9.5h30V17h-30z'/%3E%3Cpath transform='rotate(-45.001 13.257 30.507)' class='st0' d='M-1.7 26.8h30v7.5h-30z'/%3E%3C/svg%3E");
    }
    &::before {
      background-image: url("../images/products-screens/immunovit/btn-icon.png");
    }
  }
  &--orange {
    color: $orange-btn;
    &:hover {
      border-color: $orange-btn;
    }
    &::after {
      background-image: url("data:image/svg+xml,%3Csvg id='Layer_1' xmlns='http://www.w3.org/2000/svg' viewBox='0 0 26.5 43.8'%3E%3Cstyle%3E .st0%7Bfill:%23de3c16%7D %3C/style%3E%3Cpath transform='rotate(45.001 13.258 13.257)' class='st0' d='M-1.7 9.5h30V17h-30z'/%3E%3Cpath transform='rotate(-45.001 13.257 30.507)' class='st0' d='M-1.7 26.8h30v7.5h-30z'/%3E%3C/svg%3E");
    }
    &::before {
      background-image: url("../images/products-screens/smartvit/btn-icon.png");
    }
