@import "./helpers/variables";

.about-screen-wrapper {
  background-image: url("../images/about-products/background.jpg");
  background-repeat: no-repeat;
  background-position: center;
  background-size: cover;
}

.about-screen__content {
  width: 100%;
  height: 100%;
  display: flex;
  align-items: center;
  justify-content: center;
}

.about-screen__desc {
  width: 100%;
  text-align: center;
  margin-top: 5em;
  padding: 0 1em;
  visibility: visible;
  z-index: 2;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;

  @media (max-width: 375px) {
    margin-top: 2em;
  }

  @media (min-width: $desktop-sm-bp) {
    padding: 0;
    width: 870px;
    height: 100%;
  }

  &.hidden {
    opacity: 0;
    visibility: hidden;
  }
}

.about-screen__text {
  font-family: $primary-font-stack;
  color: $primary-text-color;
  line-height: 21px;
  font-weight: 400;
  text-align: center;
  font-size: calc(11px + 0.5vw);
  margin-bottom: 52px;

  @media (max-width: 375px) {
    line-height: 19px;
    margin-bottom: 25px;
  }

  @media (min-width: $desktop-sm-bp) {
    font-size: calc(12px + 0.5vw);
    line-height: 35px;
  }

  & > span {
    font-size: calc(11px + 0.5vw);
    font-family: $primary-font-stack;
    color: $primary-text-color;
    position: relative;

    &:after {
      content: "\00AE";
      display: inline-block;
      position: absolute;
      top: -7px;
      right: -8px;
      width: 6px;
      height: 6px;
    }
  }
}

.about-screen__title {
  @extend .about-screen__text;
  font-weight: 700;
  line-height: 38px;
  font-size: calc(30px + 0.5vw);
  text-transform: uppercase;
  background: linear-gradient(
    45deg,
    rgba(31, 45, 55, 1) 27%,
    rgba(78, 126, 159, 1) 60%
  ); /* W3C, IE10+, FF16+, Chrome26+, Opera12+, Safari7+ */
  filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#1f2d37', endColorstr='#4e7e9f',GradientType=1 ); /* IE6-9 fallback on horizontal gradient */
  background-clip: text;
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  margin-bottom: 28px;

  @media (max-width: 375px) {
    font-size: calc(26px + 0.5vw);
  }

  @media (min-width: $desktop-sm-bp) {
    line-height: 70px;
    font-size: calc(40px + 0.5vw);
    margin-bottom: 52px;
  }

  @media (min-width: $desktop-md-bp) {
    font-size: calc(50px + 0.5vw);
  }
}

.about-screen__btn {
  @extend .about-screen__title;
  display: inline-block;
  font-size: calc(15px + 0.5vw);
  line-height: unset;
  border: 3px solid $primary-text-color;
  border-radius: 64px;
  padding: 15px 39px;
  position: relative;
  cursor: pointer;

  @media (min-width: $desktop-sm-bp) {
    font-size: calc(17px + 0.5vw);
    padding: 20px 110px;
  }

  @media (min-width: $desktop-lg-bp) {
    font-size: calc(20px + 0.5vw);
    padding: 35px 130px;
  }

  &::after {
    content: "";
    display: inline-block;
    background-image: url("data:image/svg+xml,%3Csvg id='Layer_1' xmlns='http://www.w3.org/2000/svg' viewBox='0 0 26.5 43.8'%3E%3Cstyle%3E .st0%7Bfill:%23403e3e%7D %3C/style%3E%3Cpath transform='rotate(45.001 13.258 13.257)' class='st0' d='M-1.7 9.5h30V17h-30z'/%3E%3Cpath transform='rotate(-45.001 13.257 30.507)' class='st0' d='M-1.7 26.8h30v7.5h-30z'/%3E%3C/svg%3E");
    background-repeat: no-repeat;
    background-position: center;
    position: absolute;
    align-self: center;
    top: 48%;
    right: 6%;
    transform: translate(-50%, -40%);
    width: 14px;
    height: 14px;

    @media (min-width: $desktop-sm-bp) {
      width: 20px;
      height: 20px;
      right: 12%;
    }

    @media (min-width: $desktop-lg-bp) {
      top: 49%;
      transform: translate(-50%, -40%);
      width: 30px;
      height: 30px;
    }
  }
}

sources.scss\\components\\product-page.scss:
@import "./main-page.scss";
@import "../helpers/variables";
@import "../components/slider-control";
.product-page__main-screen {
  @extend .main-page__screen;

  @media (max-width: 567px) {
    height: 100vh;
  }

  @media (min-width: 1366px) and(min-height: 1024px) {
    height: 70vh !important;
  }
}

.product-page__inner {
  @extend .main-page__inner;
  background-image: url("../images/product-page/light-481.png");
  width: 100%;
  background-repeat: no-repeat;
  background-position: center;
  background-size: contain;
  @media (min-width: 375px) and (max-width: 400px),
    (min-width: 468px),
    (-webkit-min-device-pixel-ratio: 2),
    (min-resolution: 192dpi) {
    background-image: url("../images/product-page/light.png");
  }
}

.product-page__title-wrapper {
  text-align: center;
  margin: 5em 0 2em;

  @media (min-width: $desktop-sm-bp) {
    margin: 2em 0 2.5em;
  }
}

.product-page__title {
  @extend .main-screen__title;
  align-self: center;
  font-weight: 700;
  text-transform: uppercase;
  font-family: $primary-font-stack;
  font-size: calc(30px + 0.5vw);
  margin-bottom: 30px;
  background: rgb(61, 165, 107); /* Old browsers */

  background: linear-gradient(
    135deg,
    rgba(61, 165, 107, 1) 17%,
    rgba(61, 165, 107, 1) 36%,
    rgba(69, 125, 171, 1) 48%,
    rgba(171, 79, 135, 1) 52%,
    rgba(223, 67, 7, 1) 60%,
    rgba(223, 67, 7, 1) 100%
  ); /* W3C, IE10+, FF16+, Chrome26+, Opera12+, Safari7+ */
  filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#3da56b', endColorstr='#df4307',GradientType=1 ); /* IE6-9 fallback on horizontal gradient */
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;

  @media (min-width: $desktop-sm-bp) {
    font-size: calc(40px + 1vw);
  }

  @media (min-width: $desktop-lg-bp) {
    font-size: calc(75px + 1vw);
  }
}

.product-page__vitamins {
  margin: 0 auto;
  display: flex;
  align-items: center;
  max-width: 55%;

  @media (max-width: 567px) {
    max-width: 85%;
  }

  @media (min-width: $desktop-sm-bp) {
    max-width: 31%;
    align-items: flex-end;
  }

  @media (min-width: $desktop-lg-bp) {
    max-width: 38%;
  }
}

.product-page__subtitle {
  @extend .main-screen__subtitle;
  text-transform: uppercase;
  top: 0;
  font-size: 11px;
  letter-spacing: 2px;
  @media (min-width: $desktop-sm-bp) {
    font-size: 23px;
  }
}

.product-page__about {
  @extend .main-page__screen;
  @media (min-width: 1366px) and(min-height: 1024px) {
    height: 100%;
  }
}

.product-page__slider-control {
  display: flex;
  align-items: center;
  justify-content: space-between;
  position: absolute;
  pointer-events: none;
  top: 50%;
  left: 0;
  transform: translateY(-50%);
  width: 100%;
  padding: 0 2em;
  z-index: 20;

  & > button {
    height: 50px;
    width: 50px;
    cursor: pointer;
    color: #acacac;
    transition: 0.1s linear;
    pointer-events: auto;

    &:hover {
      color: #464646;
    }
  }
}

.product-page__fruits {
  @extend .main-page__fruits;
  height: 100%;

  & > ul:first-child {
    top: -20% !important;
    left: 10%;
    width: 50%;
    height: 100%;
    li {
      position: absolute !important;
    }
  }
  & > ul:last-child {
    right: -10%;
    width: 50%;
    height: 100%;
    li {
      position: absolute !important;
    }
  }
}

.smartvit-fruits {
  & > ul:first-child {
    li {
      &:first-child {
        max-width: 40%;
        top: -35% !important;
      }
      &:last-child {
        max-width: 20%;
        top: 10% !important;
        left: 40% !important;
      }
    }
  }
  & > ul:last-child {
    li {
      position: absolute !important;
      top: 0;
      &:first-child {
        max-width: 20%;
        top: -45% !important;
      }
      &:last-child {
        max-width: 40%;
        top: -20% !important;
      }
    }
  }
}

.calcivit-fruits {
  & > ul:first-child {
    li {
      &:first-child {
        max-width: 30%;
        top: -30% !important;
        left: -20% !important;
      }
      &:nth-child(2) {
        top: 5% !important;
        max-width: 6%;
      }
      &:nth-child(3) {
        top: 10% !important;
        left: 40% !important;
      }
      &:last-child {
        max-width: 9%;
        top: -25% !important;
        left: 45% !important;
      }
    }
  }
  & > ul:last-child {
    li {
      position: absolute !important;
      top: 0;
      &:first-child {
        max-width: 20%;
        top: -60% !important;
      }
      &:nth-child(2) {
        top: -47% !important;
        left: -19% !important;
        max-width: 6%;
      }
      &:nth-child(3) {
        top: -30% !important;
        max-width: 17%;
      }
      &:last-child {
        max-width: 7%;
        top: 0% !important;
        left: 30% !important;
      }
    }
  }
}

.multivit-fruits {
  @extend .calcivit-fruits;
  & > ul:last-child {
    li {
      &:nth-child(3) {
        top: -30% !important;
        max-width: 30%;
      }
    }
  }
}

.hello-kitty-fruits {
  @extend .calcivit-fruits;

  & > ul:first-child {
    li {
      &:first-child {
        max-width: 17%;
        top: -30% !important;
        left: -3% !important;
      }

      &:nth-child(2) {
        top: 5% !important;
        max-width: 23%;
      }

      &:last-child {
        max-width: 9%;
        top: -4% !important;
        left: 30% !important;
      }
    }
  }

  & > ul:last-child {
    li {
      &:first-child {
        max-width: 23%;
        top: -60% !important;
        left: 22% !important;
      }

      &:nth-child(2) {
        top: -35% !important;
        left: 0% !important;
        max-width: 7%;
      }

      &:last-child {
        max-width: 20%;
        top: 0% !important;
        left: 15% !important;
      }
    }
  }
}

sources.scss\\components\\up-button.scss:
@import "../helpers/variables";
.up-button {
  display: none;
  position: fixed;
  right: 4%;
  bottom: 8%;
  width: 60px;
  height: 60px;
  z-index: 999;
  cursor: pointer;
  user-select: none;
  background-image: url("data:image/svg+xml,%3Csvg data-name='Layer 1' xmlns='http://www.w3.org/2000/svg' viewBox='0 0 256 256'%3E%3Cdefs%3E%3ClinearGradient id='a' y1='128' x2='256' y2='128' gradientUnits='userSpaceOnUse'%3E%3Cstop offset='0' stop-color='%23ca5424'/%3E%3Cstop offset='.26' stop-color='%23966395'/%3E%3Cstop offset='.51' stop-color='%23458fa0'/%3E%3Cstop offset='1' stop-color='%2345a575'/%3E%3C/linearGradient%3E%3C/defs%3E%3Cpath d='M128 0a128 128 0 1 0 128 128A128 128 0 0 0 128 0z' fill='url(%23a)'/%3E%3Cpath fill='%23fff' d='M186.32 141.73L128 83.4l-58.32 58.33 18.87 18.87L128 121.14l39.45 39.46 18.87-18.87z'/%3E%3C/svg%3E");

  @media (min-width: $desktop-sm-bp) {
    display: block;
  }

  &:hover {
    animation: bounce 0.7s;
  }
}

@keyframes bounce {
  0%,
  20%,
  60%,
  100% {
    -webkit-transform: translateY(0);
    transform: translateY(0);
  }

  40% {
    -webkit-transform: translateY(-20px);
    transform: translateY(-20px);
  }

  80% {
    -webkit-transform: translateY(-10px);
    transform: translateY(-10px);
  }
}

sources.scss\\components\\footer.scss:
@import "../helpers/variables";
.footer {
  background-repeat: no-repeat;
  background-position: center;
  background-size: cover;
  background-color: $primary-bg-color;
  padding: 1em;
  height: 60vh;

  @media (min-width: $landscape-bp) {
    height: 100vh;
  }

  @media (min-width: $desktop-sm-bp) {
    padding: 1.5em;
  }

  @media (min-width: 1024px) and (min-height: 1366px) {
    height: 60vh !important;
  }

  &__inner {
    background-color: #fff;
    width: 100%;
    height: 100%;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;

    & > img {
      margin: 0 auto 55px;
      width: 90%;

      @media (min-width: $desktop-sm-bp) {
        margin: 0 auto 110px;
        width: 80%;
      }
    }
  }

  &__socials {
    display: flex;
    width: 200px;
    justify-content: space-between;
    align-items: center;

    @media (min-width: $desktop-sm-bp) {
      width: 295px;
    }

    &-item {
      width: 57px;
      height: 57px;
      cursor: pointer;
      transition: 0.2s linear;
      &:hover {
        transform: scale(1.1);
      }

      @media (min-width: $desktop-sm-bp) {
        width: 80px;
        height: 80px;
      }
    }
  }
}
